			\coordinate (E) at ($(O)!2!(A)$);  % Đối xứng của O qua A
			\coordinate (K) at ($(B)!1/3!(E)$); % K chia BE theo tỉ lệ 1/3
			\coordinate (H) at ($(B)!2/3!(C)$); % H chia BC theo tỉ lệ 2/3

			% Vẽ hình bình hành ABCD
			\draw (A) -- (B) -- (C) -- (D) -- cycle;

			% Đường chéo của hình bình hành
			\draw (A) -- (C);
			\draw (B) -- (D);

			% Các cạnh ngoài
			\draw (E) -- (A);
			\draw (E) -- (B);
			\draw (O) -- (C);
			\draw (O) -- (D);
			\draw [->] (D)--(K);
			% Vẽ các điểm và nhãn
			\foreach \x/\pos in {A/below, B/above, C/above, D/below, O/above, E/below left, K/above left, H/above}
			\fill (\x) circle (1pt) node[\pos] {$\x$};

			% Góc 60 độ

		\end{tikzpicture}
		\end{center}
		\begin{enumerate}
			\item
			\begin{itemize}
				\item \textbf{Cách 1.} Ta có $\overrightarrow{A C}=\overrightarrow{A B}+\overrightarrow{A D} \Rightarrow A C^2=A B^2+A D^2+2 \overrightarrow{A B} \cdot \overrightarrow{A D}$.
				$$
				A C^2=9^2+15^2+2 \cdot 9 \cdot 15 \cdot \cos 60^{\circ}=441 \Rightarrow A C=21.
				$$
				\item \textbf{Cách 2.} Ta có $\widehat{A B C}=180^{\circ}-\widehat{B A D}=120^{\circ}$.\\
				$$ A C^2=A B^2+B C^2-2 A B \cdot B C \cdot \cos \widehat{A B C} =9^2+15^2-2 \cdot 9 \cdot 15 \cdot \cos 120^{\circ}=441.$$
				Suy ra $ AC=21 $.
			\end{itemize}
			Ta có $
			S_{ B C D}=2 S_{A B D}=2 \cdot \dfrac{1}{2} A B \cdot A D \cdot \sin \widehat{B A D}=9 \cdot 15 \cdot \sin 60^{\circ}=\dfrac{135 \sqrt{3}}{2}.
			$
			\item
			\begin{eqnarray*}
				& \overrightarrow{D K}=\overrightarrow{B K}-\overrightarrow{B D}=&\dfrac{1}{3} \overrightarrow{B E}+\overrightarrow{A B}-\overrightarrow{A D} \\
				& &=\dfrac{1}{3} \overrightarrow{B C}+\dfrac{1}{3} \overrightarrow{C E}+\overrightarrow{A B}-\overrightarrow{A D}\\
				& &=\dfrac{1}{3} \overrightarrow{A D}-\dfrac{1}{2} \overrightarrow{A C}+\overrightarrow{A B}-\overrightarrow{A D}=\dfrac{1}{2} \overrightarrow{A B}.
			\end{eqnarray*}
			\item
			Ta có
			$\dfrac{S_{B K O}}{S_{B E O}}=\dfrac{B K}{B E}=\dfrac{1}{3} \Rightarrow S_{B K O}=\dfrac{1}{3} S_{B E O}=\dfrac{1}{3} S_{AB C} $.\\
			Và $\dfrac{S_{B O H}}{S_{B O C}}=\dfrac{B H}{B C}=\dfrac{2}{3} \Rightarrow S_{B O H}=\dfrac{2}{3} S_{B O C}=\dfrac{1}{3} S_{AB C}$.\\
			Do đó $S_{B K O H}=S_{B K O}+S_{B O H}=\dfrac{2}{3} S_{A B C}=\dfrac{1}{3} S_{A B C D}=\dfrac{45 \sqrt{3}}{2}$.
		\end{enumerate}
	}
\end{bt}

