                \coordinate (F) at ($(C)!0.5!(D)$);
                \coordinate (K) at ($(F)!0.25!(E)$);
                \coordinate (M) at ($(C)!0.25!(B)$);
                \coordinate (N) at ($(D)!0.25!(A)$);
                \coordinate (P) at ($(D)!0.25!(S)$);
                \coordinate (Q) at ($(C)!0.25!(S)$);
                \coordinate (H) at ($(F)!0.25!(S)$);
