\begin{ex}%[2D1V1-5]%[Lớp 12- Kiểm tra giữa học kỳ - THPT Chuyên Lê Quý Đôn - Đà Nẵng]%[Nguyễn Văn Sang]
	Trong $25$ phút theo dõi, lưu lượng nước của một con sông được tính theo công thức
	$$
	Q(t)=-\dfrac{t^3}{10}+5 t^2+50.
	$$
	Trong đó $Q$ tính theo $\mathrm{m}^3/$ phút, $t$ tính theo phút, $0 \leq t \leq 25$. Khi lưu lượng nước của con sông lên đến $500 \mathrm{~m}^3 / \mathrm{phút}$ thì cảnh báo lũ được đưa ra.
	\choiceTF
	{\True Lưu lượng nước sông lúc bắt đầu theo dõi là $50 \mathrm{~m}^3 /$ phút}
	{Cảnh báo lũ được đưa ra trước thời điểm $t=10$ phút}
	{Trong khoảng thời gian $10$ phút đầu tiên, không có thời điểm nào mà tốc độ tăng của lưu lượng nước sông bằng $40 \mathrm{~m}^3/(\text{phút})^2$}
	{\True Trong khoảng thời gian từ phút thứ $10$ đến phút thứ $15$, lưu lượng nước sông trung bình mỗi phút tăng $77{,}5 \mathrm{~m}^3 /$ phút}
	\loigiai{
	 \begin{itemchoice}
	 \itemch \textbf{Đúng} vì\\
	Khi $t = 0$, ta có
		 $$
		 Q(0) = -\dfrac{0^3}{10} + 5 \cdot 0^2 + 50 = 50 \mathrm{~m}^3/\text{phút}.
		 $$
	 \itemch \textbf{Sai} vì\\
	 Xét hàm số $Q(t)=-\dfrac{t^3}{10}+5 t^2+50$, có $Q'(t)=-\dfrac{3t^2}{10}+10t$.\\
	 Cho $Q'(t)=0\Leftrightarrow \hoac{&t=0 & (\text{nhận vì } t\in \left[0;25\right])\\&t=\dfrac{100}{3}&(\text{loại vì } t\in \left[0;25\right]).}$\\
	Bảng biến thiên
	\begin{center}
		\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
			\tkzTabInit[nocadre=false,lgt=1.25,espcl=2,deltacl=0.75]
			{$t$/.7 ,$Q'(t)$/.7,$Q(t)$/2}
			{$0$,$ $ , $25$}
			\draw[dashed,->](3,-.57)node[above] {$10$}--(3,-2.75)node[below] {$450$};
			\tkzTabLine{ ,  , + , , }

			\draw[->] (2,-3.13)node[left] {$50$} -- (5.5,-1.75)node[right] {$1612{,}5$};
		\end{tikzpicture}
	\end{center}
	Dựa vào bảng biến thiên, $10$ phút đầu tiên lưu lượng nước chưa đạt mức cảnh báo là $500 \mathrm{~m}^3 / \mathrm{phút}$.
	 \itemch \textbf{Sai} vì
	 \begin{itemize}
		\item Hàm tốc độ thay đổi của lưu lượng nước là đạo hàm của $Q(t)$, ta có
		$$
		Q'(t) = -\dfrac{3t^2}{10} + 10t, \mathrm{~m}^3/(\text{phút})^2.
		$$
		\item Giải phương trình $Q'(t) = 40$ ta có 
		$$
		-\dfrac{3t^2}{10} + 10t = 40 \Leftrightarrow
		-3t^2 + 100t - 400 = 0 \Leftrightarrow \hoac{&t \approx 28{,}68 &(\text{loại vì } t \leq 25)\\&t\approx 4{,}65 &(\text{nhận vì } 0\leq t \leq 25).}
		$$
		\item Nhận thấy $t \approx 4{,}65$ thuộc khoảng $10$ phút đầu, nên tồn tại thời điểm trong $10$ phút đầu mà $Q'(t) = 40 \mathrm{~m}^3/(\text{phút})^2$.
	\end{itemize}
	 \itemch \textbf{Đúng} vì
	 \begin{itemize}
		\item Tính lưu lượng tại $t = 10$ và $t = 15$, ta có\\
		$
		Q(10) = -\dfrac{1000}{10} + 5 \cdot 100 + 50 = -100 + 500 + 50 = 450 \mathrm{~m}^3/\text{phút}.
		$\\ 
		$
		Q(15) = -\dfrac{3375}{10} + 5 \cdot225 + 50 = -337{,}5 + 1125 + 50 = 837{,}5 \mathrm{~m}^3/\text{phút}.
		$
		\item Lưu lượng trung bình $Q_{\text{trung bình}}$ trong khoảng $t$ từ $10$ đến $15$:
		$$
		Q_{\text{trung bình}} = \dfrac{Q(15) - Q(10)}{15 - 10} = \dfrac{837{,}5 - 450}{5} = \dfrac{387{,}5}{5} = 77{,}5 \mathrm{~m}^3/\text{phút}.
		$$
	\end{itemize}
	 \end{itemchoice}
	}
\end{ex}

\begin{ex}%[2H2N2-1]%[2-TN-DS-TLN-THPT-ChuyenLeQuyDon-DaNang-HKI-NH24-25]%[Lê Linh Đan]
	Cho tam giác $DEF$, biết $D(1;2;-1)$, $E(2;-1;0)$, $F(0;2;-5)$ và $G$ là trọng tâm. Tính độ dài của đoạn $DG$ (làm tròn kết quả đến hàng phần trăm).
	\shortans[oly]{1,14}
	\loigiai{
		$G$ là trọng tâm tam giác $DEF$ nên ta có 
		\begin{align*}
			\heva{
				& x_G=\dfrac{x_D+x_E+x_F}{3}
				\\ & y_G=\dfrac{y_D+y_E+y_F}{3}
				\\ & z_G=\dfrac{z_D+z_E+z_F}{3}
			}&\Leftrightarrow
			 \heva{
				& x_G=\dfrac{1+2+0}{3}
				\\
				& y_G=\dfrac{2-1+2}{3}
				\\
				& z_G=\dfrac{-1+0-5}{3}
			}
			\\&\Leftrightarrow
			 \heva{
				& x_G=1,
				\\
				& y_G=1,
				\\ 
				& z_G=-2.
			}
		\end{align*}
		Do đó $G(1;1;-2) \Rightarrow \overrightarrow{DG}=(0;-1;-1)$.\\
		Vậy $DG=\left|\overrightarrow{DG}\right|=\sqrt{0^2+(-1)^2+(-1)^2}=\sqrt{2}\approx 1{,}14$.
	}
\end{ex}

\begin{ex}%[2D3N1-3]%[2-TN-DS-TLN-THPT-ChuyenLeQuyDon-DaNang-HKI-NH24-25]%[Lê Linh Đan]
	Một cửa hàng ghi lại số lượng sản phầm bán ra trong một ngày cho kết quả như bảng sau:
	\begin{center}
		\begin{table}[h]
			\begin{tabular}{|l|c|c|c|c|c|}
				\hline
				Số lượng sản phẩm $(x)$ &$0\leq x<4$&$4\leq x<8$&$8\leq x<12$&$12\leq x<16$&$16\leq x<20$
				\\\hline
				Số ngày &8&15&20&12&5
				\\\hline
			\end{tabular}
		\end{table}
	\end{center}
	Tìm khoảng tứ phân vị của mẫu số liệu ghép nhóm trên.
	\shortans[oly]{6,8}
	\loigiai{
		Ta có $n=60$ nên $\dfrac{n}{4}=\dfrac{60}{4}=15$ nên $Q_1$ thuộc nhóm $[4;8)$ và 
		\begin{align*}
			Q_1=4+\dfrac{15-8}{15}\cdot (8-4)=\dfrac{88}{15}.
		\end{align*}
		Ta có $\dfrac{3n}{4}=\dfrac{3\cdot 60}{4}=45$ nên $Q_3$ thuộc nhóm $[12;16)$ và 
		\begin{align*}
			Q_3=12+\dfrac{45-8-15-20}{12}\cdot (16-12)=\dfrac{38}{3}.
		\end{align*}
		Vậy khoảng tứ phân vị của mẫu số liệu là 
		\begin{align*}
			\Delta_Q=Q_3-Q_1=\dfrac{38}{3}-\dfrac{88}{15}=\dfrac{34}{5}=6{,}8.
		\end{align*}
	}

\end{ex}

\begin{ex}%[2D1N5-1]%[Dự Án Đợt 4 - De Thi HK1-2024-2025]%[Viet Hoang Pham]
	Đường cong dưới đây là đồ thị của hàm số nào?
	\begin{center}
		\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
			\clip (-2.5,-5.5) rectangle (3.5,1.5);
			\draw[->, >=stealth] (-2.5,0) -- (3,0) node[above] {$x$};
			\draw[->, >=stealth] (0,-5.5) -- (0,1) node[left] {$y$};
			\foreach \x in {-2, -1, 1, 2}
			\draw (\x, 0) node[above] {$\x$};
			\foreach \y in {-5, -1}
			\draw (0, \y) node[below right] {$\y$};
			\draw (0, -3) node[right] {$-3$};
			\draw[dashed] (-2,0) -- (-2,-5);
			\draw[dashed] (1,-5) -- (-2,-5);
			\draw[dashed] (1,0) -- (1,-5);
			\draw[dashed] (-1,0) -- (-1,-1);
			\draw[dashed] (2,-1) -- (-1,-1);
			\draw[dashed] (2,0) -- (2,-1);
			\draw[fill=black] (0,0) circle (1pt) node[above left] {$O$};
			\draw[smooth, domain=-2.1:2.2, samples=100] 
			plot(\x, {pow(\x,3) - 3*\x - 3});
		\end{tikzpicture}
	\end{center}
	\choice
	{$y=x^3-3x+3$}{$y=-x^3+3x-3$}{$y=x^3-3x^2-3$}{\True $y=x^3-3x-3$}
	\loigiai{
		Hàm số cần tìm có dạng bậc 3 $y=ax^3+bx^2+cx+d$ $(a\neq 0)$.\\
		Đồ thị hàm số đi qua $(-2;-5)$ $\Leftrightarrow$ $a\cdot(-2)^3+b\cdot(-2)^2+c\cdot(-2)+d=-5. \quad (1)$\\
		Đồ thị hàm số đi qua $(-1;-1)$ $\Leftrightarrow$ $a\cdot(-1)^3+b\cdot(-1)^2+c\cdot(-1)+d=-1.\quad (2)$\\
		Đồ thị hàm số đi qua $(1;-5)$ $\Leftrightarrow$ $a\cdot1^3+b\cdot1^2+c\cdot1+d=-5. \quad (3)$\\
		Đồ thị hàm số đi qua $(2;-1)$ $\Leftrightarrow$ $a\cdot2^3+b\cdot2^2+c\cdot2+d=-1. \quad (4)$\\
		Giải hệ 4 phương trình ta được $\heva{&a=1\\&b=0\\&c=-3\\&d=-3}$ $\Rightarrow$ Hàm số cần tìm là $y=x^3-3x-3$.
	}
\end{ex}

