\begin{ex}%[2H2V1-2]%[Học kì 1 - Lớp 12 -  Đề Trường THPT Chuyên Lê Hồng Phong ]%[Trần Nguyễn Vĩnh Nghi]
	Cho lăng trụ tam giác $ABC.A'B'C'$. Đặt $\overrightarrow{AA'} = \overrightarrow{a}$, $\overrightarrow{AB} = \overrightarrow{b}$, $\overrightarrow{AC} = \overrightarrow{c}$. Gọi $I$ là điểm thuộc $CC'$ sao cho $\overrightarrow{C'I} = \dfrac{1}{3}\overrightarrow{C'C}$, điểm $G$ thỏa mãn $\overrightarrow{GB} + \overrightarrow{GA'} + \overrightarrow{GB'} + \overrightarrow{GC'} = \overrightarrow{0}$. Biểu diễn vectơ $\overrightarrow{IG}$ qua các vectơ $\overrightarrow{a}$, $\overrightarrow{b}$, $\overrightarrow{c}$. Trong các khẳng định sau, khẳng định nào đúng?
	\choice
	{
		$\overrightarrow{IG} = \dfrac{1}{3}\left(\overrightarrow{a} + \overrightarrow{b} + 2\overrightarrow{c}\right)$
	}
	{ \True
		$\overrightarrow{IG} = \dfrac{1}{4}\left(\dfrac{1}{3}\overrightarrow{a} + 2\overrightarrow{b} - 3\overrightarrow{c}\right)$
	}
	{
		$\overrightarrow{IG} = \dfrac{1}{4}\left(\overrightarrow{a} + \overrightarrow{c} - 2\overrightarrow{b}\right)$
	}
	{
		$\overrightarrow{IG} = \dfrac{1}{4}\left(\overrightarrow{b} + \dfrac{1}{3}\overrightarrow{c} - 2\overrightarrow{a}\right).$
	}
	\loigiai{
\begin{center}
		\begin{tikzpicture}[declare function={goc=-45; a=5; b=0.45*a; h=5;}]
		\path[fill=black] 
		(0,0) coordinate (A)
		(a,0) coordinate (C)
		(goc:b) coordinate (B)
		(0,h) coordinate (A')
		(a,h) coordinate (C')
		($(B)+(0,h)$) coordinate (B');
		\draw[dashed] (A)--(C);
		\draw (A)--(B)--(C)--(C')--(A')--(B')--(B) (A)--(A') (B')--(C');
		\foreach \x/\goc in {A/-180,B/-90,C/0,A'/-180,B'/-30,C'/0}
		\draw[fill=white] (\x) node[shift={(\goc:7pt)}]{$\x$} circle (1pt);
		\path ($(C')!0.33!(C)$) coordinate (I);
		\draw[fill=black] (I) node[right]{$I$} circle (0.04);
		\path (barycentric cs:A'=1,B'=1,C'=1) coordinate (G');
		\draw[fill=black] (G') node[above]{$G'$} circle (0.04);
		\path ($(B')!0.5!(C')$) coordinate (M);
		\draw[fill=black] (M) node[below]{$M$} circle (0.04);
		\draw (A') --(M);
		\draw[dashed] (B)--(G');
		\path ($(G')!0.25!(B)$) coordinate (G);
		\draw[fill=black] (G) node[right]{$G$}circle (0.04);
	\end{tikzpicture}
\end{center}
Ta có  
\begin{eqnarray*}
	& & \overrightarrow{GB}+\overrightarrow{GA'}+\overrightarrow{GB'}+\overrightarrow{GC'}=\overrightarrow{0}\\
	&\Leftrightarrow & \overrightarrow{AB}-\overrightarrow{AG}+\overrightarrow{AA'}-\overrightarrow{AG}+\overrightarrow{AB'}-\overrightarrow{AG}+\overrightarrow{AC'}-\overrightarrow{AG} =\overrightarrow{0} \\
	&\Leftrightarrow & \overrightarrow{AG}=\dfrac{1}{4}\left(\overrightarrow{AB}+\overrightarrow{AA'}+\overrightarrow{AB'}+\overrightarrow{AC'}\right)\\
		&\Leftrightarrow & \overrightarrow{AG}=\dfrac{1}{4}\left(2\overrightarrow{AB}+3\overrightarrow{AA'}+\overrightarrow{AC}\right)\\
			&\Leftrightarrow & \overrightarrow{AG}=\dfrac{1}{4}\left(2\overrightarrow{b}+3\overrightarrow{a}+\overrightarrow{c}\right).
\end{eqnarray*}
Hơn nữa ta lại có $\overrightarrow{AI}=\overrightarrow{AC}+\overrightarrow{CI}=\overrightarrow{c}+\dfrac{2}{3}\overrightarrow{a}.$\\
Vậy $\overrightarrow{IG}=\overrightarrow{AG} - \overrightarrow{AI}=-\dfrac{3}{4} \overrightarrow{c} +\dfrac{1}{12}\overrightarrow{a}+\overrightarrow{1}{2}\overrightarrow{b} = \overrightarrow{IG} = \dfrac{1}{4}\left(\dfrac{1}{3}\overrightarrow{a} + 2\overrightarrow{b} - 3\overrightarrow{c}\right). $
	}

\end{ex}

\begin{ex}%[2D3H2-2]%[THPT Chuyên Lê Hồng Phong, TPHCM]%[BCTuan, dự án A ĐỢT 4]
