\begin{ex}%[2D1V4-3]%[Dự án đề kiểm tra Khối 12 HK1 NH24-25 - Đợt 4 - Quang Vinh NT]%[THPT Đinh Tiên Hoàng - Tỉnh Ninh Bình] 
	Cho hàm số $f(x) = \dfrac{x^2-2x+5}{x-1}$ có đồ thị $(C)$.
	\choiceTF
	{Hàm số có đạo hàm là $f'(x) = \dfrac{x^2 + 2x - 3}{(x-1)^2}$}
	{\True Hàm số đồng biến trên các khoảng $(-\infty;-1)$ và $(4;+\infty)$}
	{Đồ thị hàm số có tiệm cận xiên là đường thẳng $y = x - 2$}
	{\True Có $3$ giá trị nguyên dương của $m$ để hàm số $y = f(x) - \dfrac{m}{x-1}$ có $2$ cực trị}
	\loigiai{
		\begin{itemchoice}
			\itemch  Ta có
			\begin{align*}
				f'(x) &= \dfrac{(x^2-2x+5)'(x-1)-(x^2-2x+5)(x-1)'}{(x-1)^2}\\
				&= \dfrac{(2x-2)(x-1)-(x^2-2x+5)}{(x-1)^2} = \dfrac{x^2-2x-3}{(x-1)^2}.
			\end{align*}
			\itemch  Ta có $f'(x) = 0 \Leftrightarrow x^2 - 2x - 3 = 0 \Leftrightarrow \hoac{&x=-1\\&x=3.}$\\
			Lập bảng biến thiên của hàm số
			\begin{center}
				\begin{tikzpicture}
					\tkzTabInit[lgt=1.5,espcl=2]
					{$x$/1, $f'(x)$/1, $f(x)$/2}
					{$-\infty$, $-1$, $1$, $3$, $+\infty$}
					\tkzTabLine{,+,0,-,d,-,0,+,}
					\tkzTabVar{-/ $-\infty$, +/ $-4$, -D+/$-\infty$/$+\infty$, -/ $4$, +/ $+\infty$}
				\end{tikzpicture}
			\end{center}
			Vậy hàm số đồng biến trên các khoảng $(-\infty;-1)$ và $(3;+\infty)$.
			\itemch  Ta có
			$$a=\lim\limits_{x \to \pm\infty} \dfrac{f(x)}{x} = 1.$$

			$$b=\lim\limits_{x \to \pm\infty} [f(x) - ax] =  -1.$$

			Nên đồ thị hàm số có tiệm cận xiên là đường thẳng $y = x - 1$.
			\itemch  Xét hàm số $y=g(x) = f(x) - \dfrac{m}{x-1} = \dfrac{x^2-2x+5}{x-1} - \dfrac{m}{x-1}$.

			Điều kiện $x \ne 1$.

			Ta có
			$$g'(x)=\dfrac{x^2-2x-3+m}{(x-1)^2}.$$
			Để hàm số có $2$ cực trị thì phương trình $g'(x) = 0$ có $2$ nghiệm phân biệt khác $1$
			$$\Leftrightarrow \heva{&\Delta' = 1 + 3 - m > 0\\&1^2 - 2\cdot 1 - 3 + m \ne 0}\Leftrightarrow m<4.$$
			Các giá trị nguyên dương của $m$ là $1$, $2$, $3$.
			Suy ra $m \in \{1;2;3\}$.
		\end{itemchoice}
	}
\end{ex}

