\begin{ex}%[2D1H5-1]%[Dự án A - HKI - THPT Marie Curie - TP. Hồ Chí Minh - 24-25]%[Nguyễn Ngọc Huy Trường]
	\immini{
		Cho hàm số $f(x) = \dfrac{-x^2+3x+2}{2x-2}$.
		\choiceTF
		{Tập xác định của hàm số là $\mathbb{R} \setminus \{2\}$}
		{Hàm số đồng biến trên $(-\infty;1)$ và nghịch biến trên $(1;+\infty)$}
		{Tâm đối xứng của đồ thị hàm số có tọa độ là $(1;1)$}
		{\True Đồ thị hàm số có dạng là đường cong như hình bên}
	}{
		\begin{tikzpicture}[scale=.6,>=stealth, font=\footnotesize, line join=round, line cap=round]
			\draw[->] (-2,0)--(5,0) node [below]{$x$};
			\draw[->] (0,-3.5)--(0,4.5) node [left]{$y$};
			\fill (0,0) circle (1pt) node[above left]{$O$};
			\draw[smooth,samples=200,domain=-2:0.5] plot(\x,{(-(\x)^2+3*(\x)+2)/(2*(\x)-2)});
			\draw[smooth,samples=200,domain=1.5:4] plot(\x,{(-(\x)^2+3*(\x)+2)/(2*(\x)-2)});
			\draw[dashed,smooth,samples=200,domain=-1.9:3.9] plot(\x,{((-1/2)*(\x)+1)});
			\draw[dashed] (1,-3.2)--(1,4.3);
		\end{tikzpicture}
	}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}. Điều kiện xác định: $2x-2 \ne 0 \Leftrightarrow x \ne 1$. \\
			Vậy tập xác định của hàm số là $\mathscr{D} = \mathbb{R} \setminus \{1\}$.
			\itemch \textbf{Sai}. $f'(x) = \dfrac{-2x^2+4x-10}{(2x-2)^2} = \dfrac{-2(x^2-2x+5)}{(2x-2)^2} < 0$, $\forall x \ne 1$. \\
			Vậy hàm số nghịch biến trên $(-\infty;1)$ và $(1;+\infty)$.
			\itemch \textbf{Sai}. Đồ thị hàm số có tiệm cận đứng $x = 1$. \\
			Ta có $f(x) = \dfrac{-x^2+3x+2}{2x-2} = -\dfrac{1}{2}x + 1 + \dfrac{4}{2x-2}$. \\
			Suy ra tiệm cận xiên $y = -\dfrac{1}{2}x + 1$. \\
			Ta có tâm đối xứng của đồ thị hàm số là giao điểm $I$ của hai tiệm cận. \\
			Do đó $x_I = 1\Rightarrow y_I = -\dfrac{1}{2}x_I + 1 = \dfrac{1}{2}$. \\ 
			Vậy tâm đối xứng là $I\left(1;\dfrac{1}{2}\right)$.
			\itemch \textbf{Đúng}. Đồ thị hàm số có dạng là đường cong như hình bên.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D1H3-6]%[Dự án A - HKI - THPT Marie Curie - TP. Hồ Chí Minh - 24-25]%[Nguyễn Ngọc Huy Trường]
	Một nhà sản xuất Robot xác định rằng để bán được $x$ đơn vị Robot, giá bán mỗi đơn vị (triệu đồng) phải là $p(x) = 1\,000-x$. Nhà sản xuất cũng xác định rằng tổng chi phí sản xuất $x$ đơn vị được cho bởi $C(x) = 3\,000 + 20x$. Khi đó
	\choiceTF
	{Tổng doanh thu $R(x) = x+p(x)$}
	{\True Tổng lợi nhuận $P(x) = R(x) - C(x)$}
	{\True Để tối đa hóa lợi nhuận thì công ty phải sản xuất và bán $490$ đơn vị Robot}
	{\True Giá bán mỗi đơn vị là $510$ (triệu đồng) thì công ty đạt được lợi nhuận tối đa}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}. Tổng doanh thu $R(x) = xp(x) = x(1\,000-x) = 1000x - x^2$. 
			\itemch \textbf{Đúng}. Tổng lợi nhuận $P(x) = R(x) - C(x)$.
			\itemch \textbf{Đúng}. Ta có $P(x) = R(x) - C(x) = 1\,000x - x^2 - (3\,000+20x) = -x^2 + 980x - 3\,000$. \\
			Suy ra $P'(x) = -2x + 980$. Khi đó $P'(x) = 0 \Leftrightarrow -2x + 980 = 0 \Leftrightarrow x = 490$. \\
			Ta có bảng biến thiên của $P(x)$ như sau
			\begin{center}
				\begin{tikzpicture}
					\tkzTabInit[nocadre=false,lgt=1.3,espcl=2.5,deltacl=0.7]
					{$x$/0.7,$P'(x)$/0.7,$P(x)$/1.5}
					{$0$,$490$,$+\infty$}
					\tkzTabLine{,+,0,-,}   
					\tkzTabVar{-/$-3\,000$,+/$237\,100$,-/$-\infty$}
				\end{tikzpicture}
			\end{center}
			Vậy để tối đa hoá lợi nhuận thì công ty phải sản xuất và bán $490$ đơn vị Robot.
			\itemch \textbf{Đúng}. Khi $x = 490$ thì $p(490) = 1\,000 - 490 = 510$. \\
			Vậy giá bán mỗi đơn vị là $510$ (triệu đồng) thì công ty đạt được lợi nhuận tối đa
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2H2H2-4]%[Dự án A - HKI - THPT Marie Curie - TP. Hồ Chí Minh - 24-25]%[Nguyễn Ngọc Huy Trường]
	Trong không gian $Oxyz$, cho ba vecto $\overrightarrow{a}=(1;-1;2)$, $\overrightarrow{b}=(3;0;-2)$ và $\overrightarrow{c}=(7;-1;-2)$.
	\choiceTF
