\begin{ex}%[2D1C5-8]%[Đề thi HK1 lớp 12 THPT Nguyễn Khuyến-An Giang NH 24-25]%[Nguyễn Tấn Linh]
	\immini
	{
		Một miếng nhôm có bề ngang $30$ cm được uốn cong tạo thành máng dẫn nước bằng cách chia tấm nhôm thành 3 phần bằng nhau rồi gấp $2$ bên lại theo một góc $\theta \left(0<\theta \le \dfrac{\pi}{2}\right)$ như hình vẽ dưới. Hỏi $\theta$ bằng bao nhiêu để tạo ra máng có diện tích mặt ngang $S$ lớn nhất để có thể cho nước đi qua nhiều nhất? ($\theta$ tính theo radian và làm tròn đến hàng phần trăm).\shortans[oly]{1,05}
	}
	{
		\begin{tikzpicture}[line join = round, line cap = round,>=stealth,font=\footnotesize,scale=1]
			\def \canh{2}
			\def \goc{60}
			\draw (0,0) coordinate(O)--(\canh,0) coordinate(A)--++(\canh,0) coordinate(B)--++(\canh,0) coordinate(C);
			\draw[thick] (A)--(B) (B)--++(\goc:\canh) coordinate(D) (A)--++({180-\goc}:\canh) coordinate(E);
			\path[->] pic[angle radius=3mm,draw,"$\theta$",angle eccentricity=1.5] {angle = C--B--D};
			\path[<-] pic[angle radius=3mm,draw,"$\theta$",angle eccentricity=1.5] {angle = E--A--O};
			\foreach \i in {0,1,2}\draw[<->] (\i*\canh,-0.3)--++(\canh,0) node[midway,fill=white]{$10$ cm};
		\end{tikzpicture}
	}
	\loigiai
	{
		Đặt các điểm $A$, $B$, $C$, $D$, $H$ (với $H$ là hình chiếu của $B$ lên $AD$) như hình vẽ
		\begin{center}\begin{tikzpicture}[line join = round, line cap = round,>=stealth,font=\footnotesize,scale=1]
				\def \canh{2}
				\def \goc{60}
				\draw (0,0) coordinate(O)
				-- (\canh,0) coordinate(B)
				-- ++(\canh,0) coordinate(C)
				-- ++(\canh,0) coordinate(X);
				\draw[thick]
				(B)--(C)
				(C)-- ++(\goc:\canh) coordinate(D)
				(B)-- ++({180-\goc}:\canh) coordinate(A);
				\path[->] pic[angle radius=3mm,draw,"$\theta$",angle eccentricity=1.5]
				{angle = X--C--D};
				\path[<-] pic[angle radius=3mm,draw,"$\theta$",angle eccentricity=1.5]
				{angle = A--B--O};
				\foreach \i in {0,1,2}{
					\draw[<->] (\i*\canh,-0.5)--++(\canh,0)
					node[midway,fill=white]{$10$ cm};
				}
				\coordinate (H) at ($(A)!(B)!(D)$);
				\draw (A)--(D) (B)--(H);
				\foreach \diem/\goc in {A/90,H/90,D/90,B/-90,C/-90} \fill[black](\diem) circle (1pt) ($(\diem)+(\goc:3mm)$) node{$\diem$};
		\end{tikzpicture}\end{center}
		$\Rightarrow BC=AB=CD=10$; $BH=AB\cdot\sin \theta =10\cdot\sin \theta$; $AD=BC+2AH=10+20\cdot\cos \theta $.\\
		Lúc này diện tích mặt ngang $S$ chính là diện tích hình thang cân $ABCD$
		\allowdisplaybreaks
		\begin{eqnarray*}
			S_{ABCD}&=&\dfrac{1}{2}\cdot BH\cdot \left(AD+BC\right)=\dfrac{1}{2}\cdot 10\cdot \sin \theta \cdot \left(10+20\cdot \cos \theta +10\right)\\
			&=&10\cdot \sin \theta \cdot \left(10+10\cdot \cos \theta\right)\\
			\Rightarrow {S_{ABCD}}&=&100\cdot \left(\sin \theta +\sin \theta\cdot \cos \theta\right)\\
			&=&100\cdot \left(\sin \theta +\sin \theta\cdot \sqrt{1-\sin^2\theta}\right)\text{ do }\left(0<\theta \le \dfrac{\pi}{2}\right).
		\end{eqnarray*}
		Đặt $t=\sin \theta$, $t\in \left(0;1\right]$ do $\left(0<\theta \le \dfrac{\pi}{2}\right)$.\\
		Xét hàm số $f\left(t\right)=100\left(t+t\sqrt{1-t^2}\right)$ với $t\in \left(0;1\right]$
		$$\begin{aligned}
			& \Rightarrow f'\left(t\right)=100\left(1+\sqrt{1-t^2}-\dfrac{t^2}{\sqrt{1-t^2}}\right)=0 \\
			& \Leftrightarrow 1=\dfrac{t^2}{\sqrt{1-t^2}}-\sqrt{1-t^2} \\
			& \Leftrightarrow{1^2}=\left(\dfrac{t^2}{\sqrt{1-t^2}}-\sqrt{1-t^2}\right)^2 \\
			& \Leftrightarrow 1=\dfrac{t^4}{1-t^2}+1-t^2-2t^2 \\
			& \Leftrightarrow 1-t^2=t^4+\left(1-3t^2\right)\left(1-t^2\right) \\
			& \Leftrightarrow 4t^4-3t^2=0 \\
			& \Leftrightarrow \hoac{&t^2=0\Rightarrow t=0&&(\text{loại}) \\&t^2=\dfrac{3}{4}\Rightarrow t=\dfrac{\sqrt{3}}{2}&&(\text{nhận}).} \\
		\end{aligned}$$
		Bảng biến thiên
		\begin{center}
			\begin{tikzpicture}
				\tkzTabInit[
				lgt=1.5, % chiều dài của cột
				espcl=2,deltacl=0.8
				]{$t$ /1, $f'(t)$ /1, $f(t)$ /2}{$0$, $\frac{\sqrt{3}}{2}$, $1$}
				\tkzTabLine{,+,0,-,}
				\tkzTabVar{-/, +/ $75\sqrt{3}$, -/}
			\end{tikzpicture}
		\end{center}
		$\Rightarrow \max\limits_{\left[0;1\right]}f\left(t\right)=f\left(\dfrac{\sqrt{3}}{2}\right)=75\sqrt{3}$.\\
		Do đó, $S_{ABCD}$ lớn nhất là $75\sqrt{3}$ (cm$^2$) khi $\sin \theta =\dfrac{\sqrt{3}}{2}\Rightarrow \theta =\dfrac{\pi}{3}\approx 1{,}05$.
	}
\end{ex}

