\begin{ex}%[2H2H2-4]%[2H2N2-2]%[2H2H2-2]%[2H2H2-4]%[2-TN-DS-TLN-Phan Huy Chú-Hà Nội-HKI-24-25]%[Nguyễn Hoàng Anh]
	Trong không gian $Oxyz$, cho ba điểm $M(2;3;-1)$, $N(-1;1;1)$, $P(1;m+1;2)$.
	\choiceTF
	{Khoảng cách giữa hai điểm $M$ và $N$ bằng $\sqrt{13}$}
	{Trung điểm của đoạn thẳng $MN$ là điểm $I\left(\dfrac{3}{2};2;0\right)$}
	{\True Điểm $Q$ thuộc mặt phẳng $Oxy$, nếu $M$, $N$, $Q$ thẳng hàng thì $Q$ là trung điểm của đoạn thẳng $MN$}
	{Tam giác $MNP$ vuông tại $M$ khi $m=-2$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}.\\
			Ta có $MN=\sqrt{(-1-2)^2+(1-3)^2+(1+1)^2}=\sqrt{17}$.
			\itemch \textbf{Sai}.\\
			$I$ là trung điểm của đoạn $MN$ nên $\heva{&x_I=\dfrac{2-1}{2}=\dfrac{1}{2}\\&y_I=\dfrac{3+1}{2}=2\\&z_I=\dfrac{-1+1}{2}=0.}$\\
			Vậy tọa độ điểm $I\left(\dfrac{1}{2};2;0\right)$.
			\itemch \textbf{Đúng}.\\
			Do $Q \in (Oxy)$ nên $Q(x;y;0)$.\\
			Ta có $\overrightarrow{MN}=(-3;-2;2)$, $\overrightarrow{MQ}=(x-2;y-3;1)$.\\
			$M$, $N$, $Q$ thẳng hàng khi và chỉ khi hai vectơ $\overrightarrow{MN}$ và $\overrightarrow{MQ}$ cùng phương.\\
			Hay $\dfrac{x-2}{-3}=\dfrac{y-3}{-2}=\dfrac{1}{2} \Leftrightarrow \heva{&x=\dfrac{1}{2}\\&y=2.}$\\
			Suy ra $Q\left(\dfrac{1}{2};2;0\right)$. \\
			Do đó $Q$ là trung điểm của $MN$.
			\itemch \textbf{Sai}.\\
			Ta có $\overrightarrow{MN}=(-3;-2;2)$, $\overrightarrow{MP}=(-1;m-2;3)$.\\
			Tam giác $MNP$ vuông tại $M$ khi và chỉ khi\\
			$\overrightarrow{MN}\cdot \overrightarrow{MP}=0\Leftrightarrow 3-2m+4+6=0\Leftrightarrow m=\dfrac{13}{2}$.
		\end{itemchoice}
	}
\end{ex}

