\begin{ex}%[2D3N1-2]%[2D3N2-2]%[2-TN-DS-TLN-Phan Huy Chú-Hà Nội-HKI-24-25]%[Nguyễn Hoàng Anh]
	Trong thực hành đo hiệu điện thế của mạch điện, An và Bình đã dùng hai Vôn kế khác nhau để đo, mỗi bạn tiến hành đo $10$ lần và cho kết quả như sau
	\begin{center}
		\renewcommand{\arraystretch}{1.3}
		\begin{tabular}{|>{\centering\arraybackslash}p{5.5cm}|>{\centering\arraybackslash}p{2cm}|>{\centering\arraybackslash}p{2cm}|>{\centering\arraybackslash}p{2cm}|>{\centering\arraybackslash}p{2cm}|}
			\hline
			\textbf{Hiệu điện thế đo được (Vôn)} & $[3{,}85; 3{,}90)$&$[3{,}90; 3{,}95)$&$[3{,}95; 4{,}00)$&$[4{,}00; 4{,}05)$ \\ \hline
			\textbf{Số lần An đo}  & 1 & 6 & 2 & 1 \\ \hline
			\textbf{Số lần Bình đo}  & 1 & 3 & 4 & 2 \\ \hline
		\end{tabular}
		\renewcommand{\arraystretch}{1}
	\end{center}
	\choiceTF
	{\True Khoảng biến thiên của hai mẫu số liệu là như nhau}
	{Giá trị trung bình mẫu số liệu của hai bạn bằng nhau}
	{\True Các kết quả đo được của An ổn định hơn của Bình}
	{Độ lệnh chuẩn của mẫu số liệu về kết quả đo của An lớn hơn của Bình}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Khoảng biến thiên mẫu số liệu của An là $4{,}05-3{,}85=0{,}2$.\\
			Khoảng biến thiên mẫu số liệu của An là $4{,}05-3{,}85=0{,}2$.\\
			Vậy khoảng biến thiên của hai mẫu số liệu là như nhau.
			\itemch \textbf{Sai}.
			Ta có
			\begin{center}
				\renewcommand{\arraystretch}{1.3}
				\begin{tabular}{|>{\centering\arraybackslash}p{5.5cm}|>{\centering\arraybackslash}p{2cm}|>{\centering\arraybackslash}p{2cm}|>{\centering\arraybackslash}p{2cm}|>{\centering\arraybackslash}p{2cm}|}
					\hline
					\textbf{Hiệu điện thế đo được (Vôn)} & $[3{,}85; 3{,}90)$&$[3{,}90; 3{,}95)$&$[3{,}95; 4{,}00)$&$[4{,}00; 4{,}05)$ \\ \hline
					\textbf{Giá trị đại diện}  & $3{,}875$ & $3{,}925$ & $3{,}975$ & $4{,}025$ \\ \hline
					\textbf{Số lần An đo}  & 1 & 6 & 2 & 1 \\ \hline
					\textbf{Số lần Bình đo}  & 1 & 3 & 4 & 2 \\ \hline
				\end{tabular}
			\end{center}
			\renewcommand{\arraystretch}{1}
			Giá trị trung bình mẫu số liệu của An là
			\begin{center}
				$\overline{x}_{A}=\dfrac{1\cdot 3{,}875+6\cdot 3{,}925+2\cdot 3{,}975+1\cdot 4{,}025}{10}=3{,}94$.
			\end{center}
			Giá trị trung bình mẫu số liệu của Bình là
			\begin{center}
				$\overline{x}_{B}=\dfrac{1\cdot 3{,}875+3\cdot 3{,}925+4\cdot 3{,}975+2\cdot 4{,}025}{10}=3{,}96$.
			\end{center}
			\itemch \textbf{Đúng}.\\
			Phương sai mẫu số liệu của An là
			\begin{eqnarray*}
				\sigma^2_{A}&=&\dfrac{1\cdot (3{,}875-3{,}94)^2+6\cdot (3{,}925-3{,}94)^2+2\cdot (3{,}975-3{,}94)^2+1\cdot (4{,}025-3{,}94^2)}{10}\\
				&=&0{,}001525.
			\end{eqnarray*}
			Phương sai mẫu số liệu của Bình là
			\begin{eqnarray*}
				\sigma^2_{B}&=&\dfrac{1\cdot (3{,}875-3{,}96)^2+3\cdot (3{,}925-3{,}96)^2+2\cdot (3{,}975-3{,}96)^2+4\cdot (4{,}025-3{,}96^2)}{10}\\
				&=&0{,}002025.
			\end{eqnarray*}
			$\sigma^2_{A}<\sigma^2_{B}$ nên các kết quả đo được của An ổn định hơn của Bình.
			\itemch \textbf{Sai}.\\
			Độ lệch chuẩn của mẫu số liệu của An là $\sigma_{A}=\sqrt{\sigma^2_{A}}=\sqrt{0{,}001525} \approx 0{,}039$.\\
			Độ lệch chuẩn của mẫu số liệu của Bình là $\sigma_{B}=\sqrt{\sigma^2_{B}}=\sqrt{0{,}002025} = 0{,}045$.\\
			Suy ra $\sigma_{A}<\sigma_{B}$.

		\end{itemchoice}
	}
\end{ex}

