\begin{ex}%[2D1V5-3]%[Dự án đề kiểm tra Toán 12 HKI NH24-25- Võ Thị Thùy Trang]%[THPT - TRẦN PHÚ]
	Cho hàm số bậc ba $y=f(x)=ax^3+bx^2+c x+d$ có đồ thị như hình vẽ dưới đây
	\begin{center}
		\begin{tikzpicture}[line cap=butt,line join=miter,>=stealth]
			\draw[->] (-2,0)--(3.5,0)
			node[shift={(-100:7pt)},font=\normalsize]{$x$};
			\draw[->] (0,-3)--(0,3)
			node[shift={(180:7pt)},font=\normalsize]{$y$};
			\draw (5pt,0)|- (0,5pt) (0,0) node[shift={(225:9pt)},font=\normalsize]{$O$};
			\draw[dashed] (2,0)node[above]{$2$}--(2,-2)--(0,-2)node[left]{$-2$};
			\draw (0,2)node[above right]{$2$};
			\fill (2,-2) circle(1pt)
			(0,0) circle(1pt)
			(0,2) circle(1pt)
			;
			\tikzset{declare function={f(\x)=(\x)^3-3*(\x)^2+2;}}
			\begin{scope}
				\clip (-2,-3) rectangle (3.5,3);
				\draw[samples=100] plot[domain=-1.1:3.1] (\x,{f(\x)});
			\end{scope}
		\end{tikzpicture}
	\end{center}
	\choiceTF
	{\True Hàm số đạt cực tiểu tại $x=2$}
	{\True Gọi $M$ và $m$ lần lượt là giá trị lớn nhất và giá trị nhỏ nhất của hàm số $y=f(x)$ trên $[0;2]$ thì $M+m=0$}
	{\True Có $3$ giá trị nguyên của $m$ để phương trình $f(x)=m$ có $3$ nghiệm phân biệt}
	{\True Đường cong trên là đồ thị hàm số $f(x)=x^3-3x^2+2$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Hàm số đạt cực tiểu tại $x=2$.
			\itemch \textbf{Đúng}.\\
			Ta có $M=\max\limits_{[0;2]} f(x)=f(0)=2$, $m=\min\limits_{[0;2]} f(x)=f(2)=-2$.\\
			Do đó $M+m=2-2=0$.
			\itemch \textbf{Đúng}.\\
			Ta có phương trình $f(x)=m$ có $3$ nghiệm phân biệt khi và chỉ khi $-2<m<2$.\\
			Suy ra có $3$ giá trị nguyên của $m$ là $-1$, $0$, $1$.
			\itemch \textbf{Đúng}.\\
			Do $y=f(x)=ax^3+bx^2+cx+d$ nên $f'(x)=3ax^2+2bx+c$.\\
			Ta có $\heva{&f'(0)=0\\&f'(2)=0\\&f(0)=2\\&f(2)=-2}$ nên $\heva{&c=0\\&12a+4b+c=0\\&d=2\\&8a+4b+2c+d=-2}\Leftrightarrow\heva{&a=1\\&b=-3\\&c=0\\&d=2.}$\\
			Do đó đường cong trên là đồ thị hàm số $f(x)=x^3-3x^2+2$.
		\end{itemchoice}
	}
\end{ex}

