	{\True $\left(\overrightarrow{a},\overrightarrow{b}\right)=60^{\circ}$}
	{$\left(\overrightarrow{a},\overrightarrow{b}\right)=45^{\circ}$}
	{$\left(\overrightarrow{a},\overrightarrow{b}\right)=120^{\circ}$}
	\loigiai{
		Ta có $\cos \left(\overrightarrow{a},\overrightarrow{b}\right)=\dfrac{\overrightarrow{a}\cdot\overrightarrow{b}}{\left|\overrightarrow{a}\right| \cdot \left|\overrightarrow{b}\right|}=\dfrac{\dfrac{1}{2}\left|\overrightarrow{a}\right| \cdot \left|\overrightarrow{b}\right|}{\left|\overrightarrow{a}\right| \cdot \left|\overrightarrow{b}\right|}=\dfrac{1}{2}$.
		Suy ra $\left(\overrightarrow{a},\overrightarrow{b}\right)=60^{\circ}$.
