\begin{ex}%[1D2C3-8] %[1-TN-DS-TLN-ChuyenLeQuyDon-NinhThuan-HKI-NH24-25]%[Lê Phúc]
	Cho hình vuông $(H_1)$  có cạnh bằng $1$. Người ta nối các trung điểm của các cạnh hình vuông này thành hình vuông mới $(H_2)$. Từ hình vuông $(H_2)$ tiếp tục làm như trên ta nhận được dãy hình vuông $(H_1)$, $(H_2)$, $(H_3)$,$\ldots$, $(H_n)$, $\ldots$. Gọi $P_i$ là chu vi của hình vuông $(H_i)$ với $i=1$, $2$, $3$, $\ldots$, $n$, $\ldots$  
	\begin{center}
			\begin{tikzpicture}[scale=1, font=\footnotesize,line join=round, line cap=round, >=stealth]
				\coordinate (A) at (0,0);
				\coordinate (B) at (4,0);
				\coordinate (C) at (4,4);
				\coordinate (D) at (0,4);
				\coordinate (E) at ($(A)!0.5!(B)$);
				\coordinate (F) at ($(B)!0.5!(C)$);
				\coordinate (G) at ($(C)!0.5!(D)$);
				\coordinate (H) at ($(D)!0.5!(A)$);
				\coordinate (I) at ($(E)!0.5!(F)$);
				\coordinate (K) at ($(F)!0.5!(G)$);
				\coordinate (L) at ($(G)!0.5!(H)$);
				\coordinate (M) at ($(H)!0.5!(E)$);
				\coordinate (N) at ($(I)!0.5!(K)$);
				\coordinate (O) at ($(K)!0.5!(L)$);
				\coordinate (P) at ($(L)!0.5!(M)$);
				\coordinate (U) at ($(M)!0.5!(I)$);
				\draw (A)--(B)--(C)--(D)--cycle;
				\draw (E)--(F)--(G)--(H)--cycle;
				\draw (I)--(K)--(L)--(M)--cycle;
				\draw (N)--(O)--(P)--(U)--cycle;
			\end{tikzpicture}
	\end{center}
	\choiceTF
	{\True Chu vi $P_2$  của hình vuông $(H_2)$  bằng $2\sqrt{2}$}
	{\True $P_1$, $P_2$, $P_3$, $\ldots$, $P_n$, $\ldots$ lập thành cấp số nhân có cộng bội bằng $\dfrac{1}{\sqrt{2}}$}
	{Tổng $P_1+P_2+\ldots+P_10=\dfrac{62+31\sqrt{2}}{8}$}
	{Đặt $T=P_1+P_2+P_3+\ldots+P_n+\ldots$. Ta có $\displaystyle \lim T=4+8\sqrt{2}$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Hình vuông $(H_2)$ có cạnh bằng $\dfrac{1}{2}$ đường chéo hình vuông $(H_1)$.\\
			Suy ra cạnh của hình vuông $(H_2)$ là $\dfrac{1}{2}\cdot\sqrt{2}=\dfrac{\sqrt{2}}{2}.$\\ $\Rightarrow P_2=4\cdot \dfrac{\sqrt{2}}{2}=2\sqrt{2}$. 
			\itemch \textbf{Đúng}.\\
		$P_1=4$.\\
		$P_2=2\sqrt{2}=4\cdot \left(\dfrac{1}{\sqrt{2}}\right)^1$.\\
		$\ldots$\\
		$P_n=4\cdot \left(\dfrac{1}{\sqrt{2}}\right)^{n-1}$.\\
		$\ldots$\\
		Suy ra 	$P_1$, $P_2$, $P_3$, $\ldots$, $P_n$, $\ldots$ lập thành cấp số nhân có cộng bội bằng $\dfrac{1}{\sqrt{2}}$.
			\itemch \textbf{Sai}.\\
			Do $P_1$, $P_2$, $P_3$, $\ldots$, $P_n$, $\ldots$ lập thành cấp số nhân có cộng bội bằng $\dfrac{1}{\sqrt{2}}$ \\
			nên $P_1+P_2+\ldots+P_{10}=4\cdot\dfrac{1-\left(\dfrac{1}{\sqrt{2}}\right)^{10}}{1-\dfrac{1}{\sqrt{2}}}=\dfrac{62+31\sqrt{2}}{8}$.
			\itemch
			\textbf{Sai}.\\
		\begin{eqnarray*}
		\displaystyle \lim T 
		 &=&\displaystyle \lim (P_1+P_2+P_3+\ldots+P_n+\ldots)\\
		 &=&\displaystyle \lim \left(P_1\cdot \dfrac{1-q^n}{1-q}\right)\\
		 &=&\displaystyle \lim 4\cdot \dfrac{1-\left(\dfrac{1}{\sqrt{2}}\right)^n}{1-\dfrac{1}{\sqrt{2}}}\\
		 &=& \dfrac{4}{1-\dfrac{1}{\sqrt{2}}}\\
		 &=&8+4\sqrt{2}.
		\end{eqnarray*}
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[1D3V2-7]%[1-TN-DS-TLN-ChuyenLeQuyDon-NinhThuan-HKI-NH24-25]%[Lê Phúc]
	Cho hàm số $f(x)=\heva{&\dfrac{3x^2-8x-3}{x-3}\ &\text{khi}& \ x<3\\&2mx+5 \ &\text{khi}& \ x \ge 3}$ ($m$ là tham số).
	\choiceTF
	{$\displaystyle \lim_{x\to 0 }f(x)=+\infty$}
	{\True $\displaystyle \lim_{x\to 5 }f(x)=10m+5$}
	{\True $\displaystyle \lim_{x\to 3^- }f(x)=10$}
	{Với $m=\dfrac{a}{b}$ ($a$, $b\in \mathbb{R}$, $\dfrac{a}{b}$ là phân số tối giản) thì tồn tại giới hạn $\displaystyle \lim_{x\to 3 }f(x)$. Ta có $a+b=12$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}.\\
			$\displaystyle \lim_{x\to 0 }f(x)=\displaystyle \lim_{x\to 0 }\dfrac{3x^2-8x-3}{x-3}=\dfrac{3\cdot 0^2-8\cdot 0 -3}{0-3}=1$.
			\itemch \textbf{Đúng}.\\
		$\displaystyle \lim_{x\to 5 }f(x)=\displaystyle \lim_{x\to 5 }(2mx+5)=2m\cdot5+5=10m+5$.
			\itemch \textbf{Đúng}.\\
			\begin{eqnarray*}
			\displaystyle \lim_{x\to 3^- }f(x)&=&\displaystyle \lim_{x\to 3^- }\dfrac{3x^2-8x-3}{x-3}\\
			&=&\displaystyle \lim_{x\to 3^- }\dfrac{3(x-3)(x+\dfrac{1}{3})}{x-3}\\&=&\displaystyle \lim_{x\to 3^-}3(x+\dfrac{1}{3})\\&=&3\cdot(3+\dfrac{1}{3})\\&=&10.
		\end{eqnarray*}
			\itemch \textbf{Sai}.\\
		\begin{itemize}
		\item $\displaystyle \lim_{x\to 3^+ }f(x)=\displaystyle \lim_{x\to 3^+ }(2mx+5)=6m+5$.
		\item 
		$\displaystyle \lim_{x\to 3^- }f(x)=10.$
		\item $f(3)=6m+5$.
		\end{itemize}
		Để $\displaystyle \lim_{x\to 3 }f(x)$ tồn tại thì $\displaystyle \lim_{x\to 3^+ }f(x)=\displaystyle \lim_{x\to 3^- }f(x)=f(3)$.
		\\
		Suy ra $6m+5=10$.\\
		$\Rightarrow m = \dfrac{5}{6}$.\\
		Do đó $a=5$, $b=6$.\\		
		Vậy $a+b=11$.

		\end{itemchoice}
	}
\end{ex}

