\begin{ex}%[11 - Kiểm tra học kì 1-2024-2025-Chuyên Hùng Vương-Phú Thọ]%[Trần Văn Hùng]%[1D1H4-4]
	Cho hàm số $y=2\cos\left(\dfrac{\pi x}{6}\right)+11$.
	\choiceTF
	{\True Hàm số có tập xác định là $\mathbb{R}$}
	{Hàm số đã cho là hàm số lẻ}
	{\True Giá trị lớn nhất của hàm số bằng $13$}
	{Phương trình $y=11$ có đúng $8$ nghiệm trên đoạn $[0;100]$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Hàm số có tập xác định là $\mathbb{R}$.
			\itemch \textbf{Sai}.\\
			Ta có $f(-x)=2\cos\left(-\dfrac{\pi x}{6}\right)+11=2\cos\left(\dfrac{\pi x}{6}\right)+11=f(x)$, $\forall x\in \mathbb{R}$.\\
			Suy ra hàm số đã cho là hàm số chẵn.
			\itemch \textbf{Đúng}.\\
			Ta có 
			\begin{eqnarray*}
				&&-1\le \cos\left(-\dfrac{\pi x}{6}\right)\le 1, \, \forall x\in \mathbb{R}\\
				&\Rightarrow& -2\le 2\cos\left(-\dfrac{\pi x}{6}\right)\le 2\\
				&\Rightarrow& 9\le 2\cos\left(-\dfrac{\pi x}{6}\right)+11\le 13\\
				&\Rightarrow& 9\le y\le 13.
			\end{eqnarray*}
			Giá trị lớn nhất của hàm số bằng $13$ khi $\cos\left(\dfrac{\pi x}{6}\right)=1\Leftrightarrow \dfrac{\pi x}{6}=k2\pi \Leftrightarrow x=12k$, $k\in \mathbb{Z}$.
			\itemch \textbf{Sai}.\\
			Phương trình 
			\begin{eqnarray*}
				y=11&\Leftrightarrow& 2\cos\left(\dfrac{\pi x}{6}\right)+11=11\\
				&\Leftrightarrow& \cos\left(\dfrac{\pi x}{6}\right)=0\Leftrightarrow \dfrac{\pi x}{6}=\dfrac{\pi}{2}+k\pi\\
				&\Leftrightarrow & x=3+6k, k\in \mathbb{Z}.
			\end{eqnarray*}
			Xét $x\in [0;100]\Leftrightarrow 0\le 3+6k\le 100\Leftrightarrow -\dfrac{1}{2}\le k\le \dfrac{97}{6}\Rightarrow k\in \{0;1;\ldots;16\}$.\\
			Vậy phương trình $y=11$ có đúng $17$ nghiệm trên đoạn $[0;100]$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[11 - Kiểm tra học kì 1-2024-2025-Chuyên Hùng Vương-Phú Thọ]%[Trần Văn Hùng]%[1D2H2-6]
	Cho một cấp số cộng $(u_n)$ có $u_1=2$, $u_2=-7$.
	\choiceTF
	{\True Công sai của cấp số cộng $d =-9$}
	{Số hạng tổng quát của cấp số cộng là $u_n=-9n-7$}
	{Số $-60$ là một số hạng của cấp số cộng}
	{\True Tổng của $25$ số hạng đầu tiên của cấp số cộng $S_{25}=-2\,650$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Công sai cấp số cộng là $d=u_2-u_1=-7-2=-9$.
			\itemch \textbf{Sai}.\\
			Số hạng tổng quát của cấp số cộng là 
			$$u_n=u_1+(n-1)d=2+(n-1)\cdot (-9)=-9n+11,\, n\ge 1.$$
			\itemch \textbf{Sai}.\\
			Giả sử $u_n=-60\Leftrightarrow -9n+11=-60\Leftrightarrow n=\dfrac{71}{9}$ (loại).\\
			Suy ra số $-60$ không phải là một số hạng của cấp số cộng.
			\itemch \textbf{Đúng}.\\
			Tổng của $25$ số hạng đầu tiên của cấp số cộng là 
			$$S_{25}=\dfrac{25}{2}\cdot \left[2\cdot 2+24\cdot (-9)\right]=-2\,650.$$
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[1D2H1-3]%[Dự án đề kiểm tra Toán khối 11 học kì 1 NH24-25-Dot 5-Thái Bảo ]%[THPT Chuyên Hùng Vương - Phú Thọ]
	Số $\dfrac{9}{41}$ là số hạng thứ mấy của dãy số $u_n=\dfrac{2n}{n^2+1}$?
	\shortans[oly]{$9$}
	\loigiai{
		Ta có 
		$\dfrac{2n}{n^2+1}=\dfrac{9}{41}\Leftrightarrow 2n\cdot 41 = (n^2+1)\cdot 9\Leftrightarrow 82n=9n^2+9\Leftrightarrow9n^2-82n+9=0\Leftrightarrow\hoac{&n=9 \text{\,(nhận)}\\&n=\dfrac{1}{9} \text{\,(loại).}}$\\
		Vậy số $\dfrac{9}{41}$ là số hạng thứ $9$ của dãy số $u_n=\dfrac{2n}{n^2+1}$.
	}
\end{ex}

