\begin{ex}%[1D3V3-3]%[Dự án đề kiểm tra Toán khối 11 HKI NH24-25-Dot 5- Sy Truong]%[THPT Lê Quý Đôn-TP HCM]
	Cho hàm số $y=f(x)=\heva{&-\dfrac{x}{2} &&\text{khi\,} x \leq 1 \\ &\dfrac{x^2-3x+2}{x^2-1} &&\text{khi\,} x>1.}$
	\choiceTF
	{\True Hàm số $y=f(x)$ liên tục tại điểm $x_0=2$}
	{$\lim\limits_{x \to 1^+} f(x)=\dfrac{1}{2}$}
	{\True Hàm số $y=f(x)+\sin x$ không liên tục tại điểm $x_0=0$}
	{\True Hàm số $y=f(x)$ liên tục tại điểm $x_0=1$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.
			Ta có $ f(2)=\lim\limits_{x\to 2}f(x)=0 $ nên hàm số liên tục tại $ x_0=2 $.
			\itemch \textbf{Sai}.
			Ta có $\lim\limits_{x \to 1^+} f(x)=\lim\limits_{x\to 1^+}\dfrac{x^2-3x+2}{x^2-1}=\lim\limits_{x\to 1^+}\dfrac{(x-1)(x-2)}{(x-1)(x+1)}=\lim\limits_{x\to 1^+}\dfrac{x-2}{x+1}=-\dfrac{1}{2}$.
			\itemch \textbf{Đúng}.
			Ta có $ \lim\limits_{x\to 0}f(x)=f(0)=0 $ nên hàm số $ f(x) $ liên tục tại $ x_0=0 $.\\
			Mặt khác hàm số $ y=\sin x $ cũng liên tục tại $ x_0=0 $, do đó hàm số $ f(x)+\sin x $ liên tục tại $ x_0=0 $.
			\itemch \textbf{Đúng}.
			Ta có
			\begin{itemize}
				\item $f(1)=-\dfrac{1}{2} $.
				\item $\lim \limits_{x \to 1^-}f(x)=\lim\limits_{x \to 1^-} \left(-\dfrac{x}{2}\right)=-\dfrac{1}{2}$.
				\item $ \lim\limits_{x \to 1^+}f(x)=-\dfrac{1}{2}$.
			\end{itemize}
			Vì $\lim \limits_{x \to 1^-}f(x) = \lim\limits_{x \to 1^+}f(x)=f(1)=-\dfrac{1}{2}$ nên hàm số đã cho không liên tục tại $ x_0=1 $.
		\end{itemchoice}
	}
\end{ex}

