	\choiceTF
	{\True $f(x)=\dfrac{3x-2}{x-5}$ là hàm số liên tục trên mỗi khoảng $(-\infty;5)$, $(5;+\infty)$}
	{\True $f(x)=\sin x-2\cos x+3$ là hàm số liên tục trên $\mathbb{R}$}
	{Hàm số $f(x)=\heva{&-\dfrac{x}{2}&\text{khi }&x\le 1\\&\dfrac{x^2-3x+2}{x^2-1}&\text{khi }&x>1}$ là hàm gián đoạn tại điểm $x_0=1$}
	{\True Hàm số $f(x)=\dfrac{3x-2}{x-5}$ là hàm số gián đoạn tại điểm $x_0=5$}
	\loigiai{
		\begin{itemchoice}
			\itemch $f(x)=\dfrac{3x-2}{x-5}$ là hàm phân thức, có tập xác định là $\left(-\infty;5\right)\cup\left(5;+\infty\right)$ nên hàm số liên tục trên các khoảng $\left(-\infty;5\right)$ và $\left(5;+\infty\right)$.
			\itemch $f(x)=\sin x-2\cos x+3$ có tập xác định là $\mathbb{R}$ nên hàm số liên tục trên $\mathbb{R}$.
			\itemch $f(1)=-\dfrac{1}{2}$.\\ 
			$\lim\limits_{x\to1^{-}}f(x)=\lim\limits_{x\to1^{-}}\left(-\dfrac{x}{2}\right)=-\dfrac{1}{2}$.\\
			$\lim\limits_{x\to1^{+}}f(x)=\lim\limits_{x\to1^{+}}\dfrac{x^2-3x+2}{x^2-1}=\lim\limits_{x\to1^{+}}\dfrac{(x-1)(x-2)}{(x-1)(x+1)}=\lim\limits_{x\to1^{+}}\dfrac{x-2}{x+1}=-\dfrac{1}{2}$.\\
			Vì $\lim\limits_{x\to1^{+}}f(x)=\lim\limits_{x\to1^{-}}f(x)=f(1)=-\dfrac{1}{2}$ nên hàm số liên tục tại $x_0=1$.
			\itemch Vì $f(5)$ không tồn tại nên hàm số không liên tục tại $x_0=5$.
		\end{itemchoice}
	}
\end{ex}

