	\loigiai{
			Ta có
			\begin{eqnarray*}
				&3 \tan \left(x-\dfrac{\pi}{6}\right)-\sqrt{3}=0 &\Leftrightarrow \tan \left(x-\dfrac{\pi}{6}\right)=\dfrac{\sqrt{3}}{3}=\tan \dfrac{\pi}{6}\\
				& &\Leftrightarrow x-\dfrac{\pi}{6}=\dfrac{\pi}{6}+k \pi\\
				& &\Leftrightarrow x=\dfrac{\pi}{3}+k \pi \quad (k \in \mathbb{Z}).
			\end{eqnarray*}
