			\hline
			Tần số & $3$ & $4$ & $3$ & $5$ & $6$ & $2$ & $2$ & $1$ \\
			\hline
		\end{tabular}
	\end{center}
	\choiceTF
	{\True Khoảng biến thiên của mẫu số liệu trên là $40$}
	{\True Tứ phân vị thứ nhất của mẫu số liệu trên là $Q_1=19{,}4$}
	{\True Tần số tích lũy của nhóm thứ ba là $cf_3=10$}
	{Phương sai của mẫu số liệu trên là $s^2=88{,}2$}
	\loigiai{
		\begin{itemchoice}
			\itemch Khoảng biến thiên của mẫu số liệu trên là $R=50-10=40$.
			\itemch Cỡ mẫu $n=26$. Ta có $\dfrac{26}{4}=6{,}5$ suy ra nhóm $[15;20)$ chứa $Q_1$.\\
			Ta có $Q_1=15+\dfrac{6{,}5-3}{4}\cdot5\approx19{,}4$.
			\itemch  $cf_3=3+4+3=10$.
			\itemch Ta có số trung bình $$\overline{x}=\dfrac{3\cdot12{,}5+4\cdot17{,}5+3\cdot22{,}5+5\cdot27{,}5+6\cdot32{,}5+2\cdot37{,}5+2\cdot42{,}5+1\cdot47{,}5}{26}=27{,}5.$$
			Phương sai của mẫu số liệu là 
			$$s^2=\dfrac{3\cdot\left(12{,}5-27{,}5\right)^2+4\cdot\left(17{,}5-27{,}5\right)^2+\cdots+1\cdot\left(47{,}5-27{,}5\right)^2}{26}\approx90{,}38.$$
		\end{itemchoice}	
	}
