			\draw[dashed] (B)--(A)--(D) (A)--(A');
			\foreach \x/\g in {A/-90, B/-90, C/-90, A'/90, B'/90, C'/90,D/-90,D'/90}
			\fill (\x) circle (1pt) +(\g:0.3) node {$\x$};
		\end{tikzpicture}
	\end{center}
	\choiceTFt[1.5]
	{\True $\overrightarrow{AA'}+\overrightarrow{AB}+\overrightarrow{AD}=\overrightarrow{AC'}$}
	{\True Gọi $G$ là trọng tâm tam giác $D'DC$. Khi đó $\overrightarrow{AG}\cdot\overrightarrow{DB}=-\dfrac{23}{3}a^2$}
	{\True $\left|\overrightarrow{AB}+\overrightarrow{AD}+\overrightarrow{CC'}\right|=a\sqrt{29}$}
	{$\overrightarrow{AA'}\cdot\overrightarrow{AD}=12a^2$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}. Theo quy tắc hình hộp, ta có $\overrightarrow{AA'}+\overrightarrow{AB}+\overrightarrow{AD}=\overrightarrow{AC'}$.
			\begin{center}
				\begin{tikzpicture}[scale=0.9, font=\footnotesize, line join=round,line cap=round, >=stealth]
					\def\a{4}
					\def\b{1}
					\def\c{1.5}
					\def\h{3}
					\path 
					(0,0) coordinate (A)
					(A) + (\a,0) coordinate (D)
					(A) + (-\c,-\b) coordinate (B)
					(B) + (\a,0) coordinate (C)
					(A)+ (0,\h) coordinate (A')
					(B)+ (0,\h) coordinate (B')
					(C)+ (0,\h) coordinate (C')
					(D)+ (0,\h) coordinate (D')
					;
					\draw (B)--(C)--(D) (B)--(B')  (D')--(D) (C)--(C') (A')--(B')--(C')--(D')--cycle;
					\draw[dashed] (B)--(A)--(D) (C')--(A)--(A');
					\foreach \x/\g in {A/-90, B/-90, C/-90, A'/90, B'/90, C'/90,D/-90,D'/90}
					\fill (\x) circle (1pt) +(\g:0.3) node {$\x$};
				\end{tikzpicture}
			\end{center}
			\itemch \textbf{Đúng}. 
			\begin{center}
				\begin{tikzpicture}[scale=0.9, font=\footnotesize, line join=round,line cap=round, >=stealth]
					\def\a{4}
					\def\b{1}
					\def\c{1.5}
					\def\h{3}
					\path 
					(0,0) coordinate (A)
					(A) + (\a,0) coordinate (D)
					(A) + (-\c,-\b) coordinate (B)
					(B) + (\a,0) coordinate (C)
					(A)+ (0,\h) coordinate (A')
					(B)+ (0,\h) coordinate (B')
					(C)+ (0,\h) coordinate (C')
					(D)+ (0,\h) coordinate (D')
					($(D)!0.5!(D')$) coordinate (M)
					($(C)!2/3!(M)$) coordinate (G)
					($(C)!2/3!(D)$) coordinate (H)
					;
					\draw (B)--(C)--(D)(B)--(B')  (D')--(D) (D')--(C)--(C') (A')--(B')--(C')--(D')--cycle (C)--(M) (G)--(H);
					\draw[dashed] (B)--(A)--(D)--(B) (G)--(A)--(A') (A)--(H);
					\foreach \x/\g in {A/-90, B/-90, C/-90, A'/90, B'/90, C'/90,D/-90,D'/90,G/0,M/0,H/-90}
					\fill (\x) circle (1pt) +(\g:0.3) node {$\x$};
				\end{tikzpicture}
			\end{center}
			Lấy $M$ là trung điểm của $DD' \Rightarrow \dfrac{CG}{CM}=\dfrac{2}{3}$.\\
			Lấy $H$ thuộc cạnh $CD$ sao cho $\dfrac{CH}{CD}=\dfrac{2}{3}$.\\
			$\Rightarrow \dfrac{CG}{CM}=\dfrac{CH}{CD}=\dfrac{2}{3} \Rightarrow GH \parallel DD'$.\\
			Lại có $DD' \perp (ABCD) \Rightarrow GH \perp (ABCD) \Rightarrow GH \perp DB$.\\
			Ta có $\overrightarrow{AG}\cdot\overrightarrow{DB}=\left(\overrightarrow{AH}+\overrightarrow{HG}\right)\cdot \overrightarrow{DB}=\overrightarrow{AH}\cdot\overrightarrow{DB}+\overrightarrow{HG}\cdot\overrightarrow{DB}=\overrightarrow{AH}\cdot\overrightarrow{DB}$. 
