\begin{ex}%[2D3H2-2]%[Dự án A THPT NH 24-25-Dot 4-Nguyễn Ngọc Duy]
	Thầy Tuấn thống kê lại điểm trung bình cuối năm của các học sinh lớp $11A$ và $11B$ ở bảng sau:	
	\begin{center}
		\begin{tabular}{|c|wc{1.5cm}|wc{1.5cm}|wc{1.5cm}|wc{1.5cm}|wc{1.5cm}|}%Thêm gói diagbox
			\hline
			\diagbox{Lớp}{Điểm trung bình} & $[5;6]$ & $[6;7]$ & $[7;8]$ & $[8;9]$ & $[9;10]$ \\ \hline
			$11A$    & $1$     & $0$     & $11$    & $22$     & $6$ \\ \hline
			$11B$    & $0$     & $6$     & $8$     & $14$     & $12$ \\ \hline
		\end{tabular}
	\end{center}
	\choiceTF
	{Khoảng biến thiên của điểm số học sinh lớp $11A$ là $5$}
	{\True Nếu so sánh theo khoảng biến thiên thì điểm trung bình của các học sinh lớp $11B$ ít phân tán hơn điểm trung bình của các học sinh lớp $11A$}
	{Xét mẫu số liệu của lớp $11A$ ta có độ lệch chuẩn của mẫu số liệu ghép nhóm là $\sqrt{0{,}51}$}
	{\True Nếu so sánh theo độ lệch chuẩn thì học sinh lớp $11A$ có điểm trung bình ít phân tán hơn học sinh lớp $11B$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}.\\
			Điểm số học sinh lớp $11A$ có thể thấp hơn $5$ nên khoảng biến thiên của điểm số học sinh lớp $11A$ có thể lớn hơn $5$.
			\itemch \textbf{Đúng}.\\
			Khoảng biến thiên của điểm trung bình của các học sinh lớp $11A$ là $R_1=10-5=5$.\\
			Khoảng biến thiên của điểm trung bình của các học sinh lớp $11B$ là $R_2=10-6=4$.\\
			Vì $R_1>R_2$ nên nếu so sánh theo khoảng biến thiên thì điểm trung bình của các học sinh lớp $11B$ ít phân tán hơn điểm trung bình của các học sinh lớp $11A$.
			\itemch \textbf{Sai}.\\
			Chọn giá trị đại diện cho các nhóm số liệu, ta có
			\begin{center}
				\begin{tabular}{|c|wc{1.5cm}|wc{1.5cm}|wc{1.5cm}|wc{1.5cm}|wc{1.5cm}|}
					\hline
					Giá trị đại diện & $5{,}5$ & $6{,}5$ & $7{,}5$ & $8{,}5$ & $9{,}5$ \\ \hline
					$11A$    & $1$     & $0$     & $11$    & $22$     & $6$ \\ \hline
					$11B$    & $0$     & $6$     & $8$     & $14$     & $12$ \\ \hline
				\end{tabular}
			\end{center}
			Xét mẫu số liệu của lớp $11A$ ta có số trung bình của mẫu số liệu ghép nhóm là
			\[\overline{x}_1= \dfrac{5{,}5 \cdot 1 + 6{,}5 \cdot 0 + 7{,}5 \cdot 11 + 8{,}5 \cdot 22 + 9{,}5 \cdot 6}{1+0+11+22+6} = 8{,}3.\]
			Phương sai của mẫu số liệu ghép nhóm là
			\[\dfrac{1\cdot(5{,}5-8{,}3)^2+0\cdot(6{,}5-8{,}3)^2+11\cdot(7{,}5-8{,}3)^2+22\cdot(8{,}5-8{,}3)^2+6\cdot(9{,}5-8{,}3)^2}{1+0+11+22+6}=0{,}61.\]
			Độ lệnh chuẩn của mẫu số liệu ghép nhóm là $s_1=\sqrt{0{,}61}$.
			\itemch \textbf{Đúng}.\\
			Xét mẫu số liệu của lớp $11B$ ta có số trung bình của mẫu số liệu ghép nhóm là
			\[
			\overline{x}_2 = \dfrac{6{,}5 \cdot 6 + 7{,}5 \cdot 8 + 8{,}5 \cdot 14 + 9{,}5 \cdot 12}{6 + 8 + 14 + 12} = 8{,}3.
			\]			
			Phương sai của mẫu số liệu ghép nhóm là
			\[
			\dfrac{6\cdot(6{,}5-8{,}3)^2 + 8\cdot(7{,}5-8{,}3)^2 + 14\cdot(8{,}5-8{,}3)^2 + 12\cdot(9{,}5-8{,}3)^2}{6+8+14+12}= 1{,}06.
			\]		
			Độ lệch chuẩn của mẫu số liệu ghép nhóm là $s_2 = \sqrt{1{,}06}$.\\
			Vì $s_1<s_2$ nên nếu so sánh theo độ lệch chuẩn thì học sinh lớp $11A$ có điểm trung bình ít phân tán hơn học sinh lớp $11B$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2H2H2-2]%[Dự án A THPT NH 24-25-Dot 4-Nguyễn Ngọc Duy]
	Trong không gian $Oxyz$, cho hình bình hành $ABCD$, biết $A(-1; 0; 3)$, $B(2; 1; -1)$, $C(3; 2; 2)$.
	\choiceTF
	{\True Côsin góc $C$ của tam giác $ABC$ bằng $\dfrac{\sqrt{231}}{77}$}
	{\True Tọa độ của điểm $D$ là $D(0; 1; 6)$}
	{Điểm $M \in (Oxy)$ sao cho $A$, $M$, $B$ thẳng hàng có tọa độ $M \left( \dfrac{5}{4}; -\dfrac{3}{4}; 0 \right)$}
	{Tọa độ điểm $N$ thỏa mãn $\overrightarrow{NA} + \overrightarrow{NB} - 3\overrightarrow{NC} = \overrightarrow{0}$ là $N(10; 5; 4)$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Ta có $\overrightarrow{CA}=(-4;-2;1)$, $\overrightarrow{CB}=(-1;-1;-3)$.\\
			Khi đó
			\[\cos C = \cos \left(\overrightarrow{CA},\overrightarrow{CB}\right) = \dfrac{(-4)\cdot(-1) + (-2)\cdot(-1) + 1\cdot(-3)}{\sqrt{(-4)^2+(-2)^2+1^2}\cdot\sqrt{(-1)^2+(-1)^2+(-3)^2}} = \dfrac{\sqrt{231}}{77}.\]
			\itemch \textbf{Đúng}.\\
			Vì $ABCD$ là hình bình hành nên $\overrightarrow{AD}=\overrightarrow{BC} \Leftrightarrow \heva{&x_D+1=1\\&y_D=1\\&z_D-3=3} \Leftrightarrow \heva{&x_D=0\\&y_D=1\\&z_D=6.}$\\
			Vậy $D(0;1;6)$.
			\itemch \textbf{Sai}.\\
			Vì $M\in (Oxy)$ nên $M(a;b;0)$.\\
			Ta có $\overrightarrow{AB}=(3;1;-4)$, $\overrightarrow{AM}=(a+1;b;-3)$.\\
			Để $A$, $M$, $B$ thẳng hàng thì $\overrightarrow{AM}$ và $\overrightarrow{AB}$ cùng phương suy ra
			\[\dfrac{a+1}{3}=\dfrac{b}{1}=\dfrac{-3}{-4} \Leftrightarrow \heva{&a=\dfrac{5}{4}\\&b=\dfrac{3}{4}.}\]
			Vậy $M\left(\dfrac{5}{4};\dfrac{3}{4};0\right)$.
			\itemch \textbf{Sai}.\\
		Ta có
			\begin{eqnarray*}
				&&\overrightarrow{NA} + \overrightarrow{NB} - 3\overrightarrow{NC} = \overrightarrow{0}\\
				&\Leftrightarrow& \overrightarrow{NA}-\overrightarrow{NC} + \overrightarrow{NB}-\overrightarrow{NC} = \overrightarrow{NC}\\
				&\Leftrightarrow& \overrightarrow{NC}=\overrightarrow{CA}+\overrightarrow{CB}.
			\end{eqnarray*}
			Ta lại có $\overrightarrow{CA}=(-4;-2;1)$, $\overrightarrow{CB}=(-1;-1;-3)$.\\
			Suy ra $\overrightarrow{CA}+\overrightarrow{CB}=(-5;-3;-2)$.\\
			Khi đó $\heva{&3-x_N=-5\\&2-y_N=-3\\&2-z_N=-2} \Leftrightarrow \heva{&x_N=8\\&y_N=5\\&z_N=4.}$\\
			Vậy $N(8;5;4)$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2H2H2-3]%[Dự án A THPT NH 24-25-Dot 4-Nguyễn Ngọc Duy]
	Cho hình chóp $S.ABCD$ đáy là hình thang vuông tại $A$ và $D$, $SA \perp (ABCD)$. Góc giữa $SB$ và mặt phẳng đáy bằng $45^\circ$, $E$ là trung điểm của $SD$, $AB = 2a$, $AD = DC = a$. Gọi $G$ là trọng tâm của tam giác $ACE$. Chọn hệ trục tọa độ như hình vẽ.
	\begin{center}
		\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
			\coordinate (A) at (0,0);
			\coordinate (B) at (6,0);
			\coordinate (C) at (1,-2);
			\coordinate (D) at (-2,-2);
			\coordinate (S) at (0,3);
			\coordinate (E) at ($(S)!0.5!(D)$);
			\draw[->] (D)--(-2.5,-2.5) node[shift=(-30:0.3)] {$x$};
			\draw[->] (B)--(7,0) node[shift=(-110:0.3)] {$y$};
			\draw[->] (S)--(0,4) node[shift=(-150:0.3)] {$z$};
			\draw (S)--(D)--(C)--(S)--(B)--(C)--(E);
			\draw[dashed] (S)--(A)--(B) (D)--(A)--(C) (A)--(E);
			\fill (3,0) node[above]{$2a$}
			(-1,-1) node[right]{$a$}
			(-0.5, -2) node [below]{$a$};
			\path pic["\scriptsize$45^\circ$", angle eccentricity=1.5,draw,angle radius=20pt]{angle= S--B--A};
			\foreach \i/\g in {A/45,B/45,C/-45,D/135,E/135,S/45}
			\fill (\i) circle(1pt) node[shift={(\g:3mm)}] {$\i$};
		\end{tikzpicture}
	\end{center}
	\choiceTF
	{\True $\overrightarrow{SA} \cdot \overrightarrow{CB} = 0$}
	{$\overrightarrow{CE} = \overrightarrow{CD} + \overrightarrow{CS}$}
	{Tọa độ của điểm $C(a; 2a; 0)$}
	{Độ dài $BG$ là $\dfrac{a\sqrt{113}}{6}$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Vì $SA\perp (ABCD)$ nên $SA\perp BC$ do đó $\overrightarrow{SA} \cdot \overrightarrow{CB} = 0$.
			\itemch \textbf{Sai}.\\
			Vì $E$ là trung điểm của $SD$ nên $\overrightarrow{CE} = \dfrac{1}{2}\left(\overrightarrow{CD} + \overrightarrow{CS}\right)$.
			\itemch \textbf{Sai}.\\
			Dựa vào hình vẽ, ta có $A(0;0;0)$, $B(0;2a;0)$, $D(a;0;0)$.\\
			Vì $CD \parallel AB$ và $CD=\dfrac{1}{2}AB$ nên $\overrightarrow{DC}=\dfrac{1}{2}\overrightarrow{AB}$.\\
			Mà $\overrightarrow{AB}=(0;2a;0)$.\\
			Do đó $\heva{&x_C-a=0\\&y_C=a\\&z_C=0} \Leftrightarrow \heva{&x_C=a\\&y_C=a\\&z_C=0.}$\\
			Vậy $C(a;a;0)$.
			\itemch \textbf{Sai}.\\
			Xét tam giác $SAB$ vuông tại $A$ có $\widehat{B}=45^\circ$ suy ra tam giác $SAB$ vuông cân tại $A$ hay $SA=AB=2a$.\\
			Do đó $S(0;0;2a)$.\\
			Vì $E$ là trung điểm $SD$ nên $E\left(\dfrac{a}{2};0;\dfrac{a}{2}\right)$.\\
			Vì $G$ là trọng tâm tam giác $ACE$ nên $G\left(\dfrac{a}{2};\dfrac{a}{3};\dfrac{a}{6}\right)$.\\
			Khi đó $BG=\sqrt{\left(\dfrac{a}{2}-0\right)^2 + \left(\dfrac{a}{3}-2a\right)^2 + \left(\dfrac{a}{6}-0\right)^2}=\dfrac{a\sqrt{110}}{6}$.\\
			Vậy $BG=\dfrac{a\sqrt{110}}{6}$.
		\end{itemchoice}
	}
\end{ex}

