	\shortans[oly]{16,7}
	\loigiai{
		Chọn giá trị đại diện cho mẫu số liệu ta có
		\begin{center}
			\begin{tabular}{|l|c|c|c|c|c|c|c|}
				\hline
				\textbf{Trọng lượng (kg)} & {$[14; 16)$} & {$[16; 18)$} & {$[18; 20)$} & {$[20; 22)$} & {$[22; 24)$} & {$[24; 26)$} & {$[26; 28)$} \\
				\hline
				\textbf{Giá trị đại diện} & {$15$} & {$17$} & {$19$} & {$21$} & {$23$} & {$25$} & {$27$} \\
				\hline
				\textbf{Số trẻ} & $25$ & $60$ & $120$ & $105$ & $42$ & $30$ & $18$ \\
				\hline
			\end{tabular}%\vspace{.5cm}
		\end{center}
		\begin{itemize}
			\item 	Cỡ mẫu $n=25+60+120+105+42+30+18=400$.\\
			\item 	Trọng lượng trung bình của trẻ em $6$ tuổi ở một khu vực thu được là
			\[\overline{x}=\dfrac{15\cdot 25+17\cdot 60+19\cdot 120+21\cdot 105+23\cdot 42+25\cdot 30+27\cdot 18}{400}=20{,}205.\]
