			\begin{eqnarray*}
				s^2&=&\dfrac{25\cdot 15^2+60\cdot 17^2+120\cdot 19^2+105\cdot 21^2+42\cdot 23^2+30\cdot 25^2+18\cdot 27^2}{400}-(20{,}205)^2\\
				&=&8{,}457975	\approx 8{,}5.
			\end{eqnarray*}
			\item 	Độ lệch chuẩn của mẫu số liệu ghép nhóm về trọng lượng trung bình của trẻ em là $s=\sqrt{s^2}=\sqrt{8{,}457975}\approx 2{,}9$.\\

			\item	Gọi $x_1$, $x_2$, \ldots, $x_{400}$ là trọng lượng của $400$ học sinh trong mẫu số liệu gốc đã được sắp xếp theo thứ tự không giảm.\\
			Ta có $x_1$, \ldots, $x_{25} \in [14; 16)$; $x_{26}$, \ldots, $x_{85} \in [16; 18)$; $x_{86}$, \ldots, $x_{205} \in [18; 20)$; $x_{206}$, \ldots, $x_{310} \in [20; 22)$; $x_{311}$, \ldots, $x_{352} \in [22; 24)$; $x_{353}$, \ldots, $x_{382} \in [24; 26)$; $x_{383}$, \ldots, $x_{400} \in [26; 28)$.
			\item	Tứ phân vị thứ nhất của mẫu số liệu gốc là $\dfrac{1}{2}(x_{100} + x_{101}) \in [18; 20)$.\\
			Do đó, tứ phân vị thứ nhất của mẫu số liệu ghép nhóm là
			\begin{align*}
				Q_1=a_{p}+\left(\dfrac{\dfrac{n}{4}-\left(m_1+\cdots+m_{p-1}\right)}{m_{p}}\right)\cdot\left(a_{p+1}-a_{p}\right)
				=14+\left(\dfrac{100-25}{60}\right)\cdot2=16{,}5.
			\end{align*}
			\item  Tứ phân vị thứ ba của mẫu số liệu gốc là $\dfrac{1}{2}(x_{300} + x_{301}) \in [20; 22)$.\\
			Do đó, tứ phân vị thứ ba của mẫu số liệu ghép nhóm là
			\begin{align*}
				Q_{3}=a_{p}+\left(\dfrac{\dfrac{3 n}{4}-\left(m_1+\cdots+m_{p-1}\right)}{m_{p}}\right)\cdot\left(a_{p+1}-a_{p}\right)
				=20+\left(\dfrac{300-205}{105}\right)\cdot 2\approx 21{,}8.
			\end{align*}

			Vậy khoảng tứ phân vị của mẫu số liệu là $\Delta _Q = Q_3 - Q_1  \approx 5{,}3$.\\
			Vậy $P=\Delta _Q+s^2+s \approx 16{,}7$.
		\end{itemize}
	}
\end{ex}

