\begin{ex}%[2H3H1-2]%[2-HK1- THPT Chuyên Trần Phú - Hải Phòng - Năm học 2024-2025]%[Nguyễn Hoàng Việt - Đợt 4]
	Cho 2 véc-tơ $\overrightarrow{a},\overrightarrow{b}$ biết $|\overrightarrow{a}|=|\overrightarrow{b}|=1$ và hai véc-tơ $\overrightarrow{u}=\dfrac{2}{5}\overrightarrow{a}-3\overrightarrow{b}$ và $\overrightarrow{v}=\overrightarrow{a}+\overrightarrow{b}$ vuông góc với nhau. Khi đó cosin góc giữa 2 véc-tơ $\overrightarrow{a}$ và $\overrightarrow{b}$ bằng.
	\shortans[oly]{$-1$}
	\loigiai{
		Ta có\\
		\begin{eqnarray*}
			&&\overrightarrow{u}\cdot\overrightarrow{v}=0\\
			&\Leftrightarrow& \left(\dfrac{2}{5}\overrightarrow{a}-3\overrightarrow{b}\right)\cdot\left(\overrightarrow{a}+\overrightarrow{b}\right)=0  \\
			&\Leftrightarrow& \dfrac{2}{5}\overrightarrow{a}^2-\dfrac{2}{5}\overrightarrow{a}\cdot\overrightarrow{b}-3\overrightarrow{a}\cdot\overrightarrow{b}-3\overrightarrow{b}^2=0 \\
			&\Leftrightarrow& \dfrac{2}{5}\left|\overrightarrow{a}\right|^2-\dfrac{2}{5}\overrightarrow{a}\cdot\overrightarrow{b}-3\overrightarrow{a}\cdot\overrightarrow{b}-3\left|\overrightarrow{a}\right|^2=0 \\
			&\Leftrightarrow& \dfrac{2}{5}-\dfrac{13}{5}\overrightarrow{a}\cdot\overrightarrow{b}-3=0 \\
			&\Leftrightarrow& \overrightarrow{a}\cdot\overrightarrow{b}=-1.
		\end{eqnarray*}
		Mà $\cos(\overrightarrow{a};\overrightarrow{b})=\dfrac{\overrightarrow{a}\cdot\overrightarrow{b}}{\left|\overrightarrow{a}\right|\cdot\left|\overrightarrow{b}\right|} =-1$
		nên  cosin góc giữa 2 véc-tơ $\overrightarrow{a}$ và $\overrightarrow{b}$bằng $-1$. %góc giữa 2 véc-tơ  $(\overrightarrow{a};\overrightarrow{b})= 180^{\circ} $.
	}
\end{ex}

\begin{ex}%[2H3H1-1]%[2-HK1- THPT Chuyên Trần Phú - Hải Phòng - Năm học 2024-2025]%[Nguyễn Hoàng Việt - Đợt 4]
