\begin{ex}%[2D3H2-2]%[Dự án đề kiểm tra Toán 12 HKI NH24-25-Nguyễn Hữu Duy]%[THPT Thị Xã Quảng Trị - Quảng Trị]
	Bảng số liệu ghép nhóm sau cho biết cân nặng của $42$ học sinh lớp $12C$.
	\begin{center}
		\begin{tabular}{|c|c|c|c|c|c|c|}
			\hline 
			\textbf{Cân nặng (kg)} & $[40{,}5;45{,}5)$ & $[45{,}5;50{,}5)$ & $[50{,}5;55{,}5)$ & $[55{,}5;60{,}5)$ & $[60{,}5;65{,}5)$ & $[65{,}5;70{,}5)$\\
			\hline 
			\textbf{Số học sinh} & $10$ & $7$ & $16$ & $4$ & $2$ & $3$ \\
			\hline
		\end{tabular}
	\end{center}
	\choiceTF
	{\True Giá trị đại diện của nhóm $[40{,}5;45{,}5)$ là $43$}
	{\True Số trung bình của mẫu số liệu ghép nhóm trên là $\overline{x}=\dfrac{1088}{21}$}
	{Phương sai của mẫu số liệu ghép nhóm trên là $s^2\approx 1{,}57$}
	{\True Độ lệch chuẩn của mẫu số liệu ghép nhóm trên là $s\approx 7{,}06$}
	\loigiai{
		Ta lập lại bảng số liệu như sau
		\begin{center}
			\begin{tabular}{|c|c|c|c|c|c|c|}
				\hline 
				\textbf{Cân nặng (kg)} & $[40{,}5;45{,}5)$ & $[45{,}5;50{,}5)$ & $[50{,}5;55{,}5)$ & $[55{,}5;60{,}5)$ & $[60{,}5;65{,}5)$ & $[65{,}5;70{,}5)$\\
				\hline 
				\textbf{Cân nặng đại diện} & $43$ & $48$ & $53$ & $58$ & $63$ & $68$\\
				\hline 
				\textbf{Số học sinh} & $10$ & $7$ & $16$ & $4$ & $2$ & $3$ \\
				\hline
			\end{tabular}
		\end{center}
		Cỡ mẫu $n=42$.
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Giá trị đại diện của nhóm $[40{,}5;45{,}5)$ là $43$.
			\itemch \textbf{Đúng}.\\
			Số trung bình của mẫu số liệu ghép nhóm trên là
			\begin{align*}
				\overline{x}=\dfrac{43\cdot 10+48\cdot 7+53\cdot 16+58\cdot 4+63\cdot 2+68\cdot 3}{42}
				= \dfrac{1088}{21}.
			\end{align*}
			\itemch \textbf{Sai}.\\
			Phương sai của mẫu số liệu ghép nhóm trên là
			\begin{align*}
				s^2=\dfrac{43^2\cdot 10+48^2\cdot 7+53^2\cdot 16+58^2\cdot 4+63^2\cdot 2+68^2\cdot 3}{42} - \overline{x}^2
				= \dfrac{21950}{441}\approx 49{,}77.
			\end{align*}
			\itemch \textbf{Đúng}.\\
			Độ lệch chuẩn của mẫu số liệu ghép nhóm trên là $s=\sqrt{\dfrac{21950}{441}}=\dfrac{5\sqrt{878}}{21}\approx 7{,}06$.
		\end{itemchoice}
	}
\end{ex}

