			\draw[->,thick] (O)--($(A)!0.5!(O)$) node[pos=1,left] {$\overrightarrow{F}_1$};
			\draw[->,thick] (O)--($(B)!0.5!(O)$) node[pos=1,left] {$\overrightarrow{F}_2$};
			\draw[->,thick] (O)--($(C)!0.5!(O)$) node[pos=1,right] {$\overrightarrow{F}_3$};
			\draw[->,thick] (J)--(0,-2.5) node[pos=0.5,right] {$\overrightarrow{P}$};
		\end{tikzpicture}
	\end{center}
	\shortans[oly]{18,4}
	\loigiai{
		\begin{center}
			\begin{tikzpicture}[scale=1.5, font=\footnotesize, line join=round, line cap=round, >=stealth]
				\path 
				(0,2) coordinate (O)
				(0,-1) coordinate (I)
				++(90:-.6) coordinate (J)
				(-2,-1) coordinate (A)
				(2,-1) coordinate (C)
				(-0.5,-.5) coordinate (B) 
				(2,.5) coordinate (L)
				($(O)!2/3!(C)$) coordinate (K)
				(2,-.25) coordinate (M)
				($(I)!.5!(C)$) coordinate (N)
				;
				% Vẽ đáy hình nón
				\draw[dashed] (A) arc[start angle=180,end angle=0,x radius=2cm,y radius=0.5cm]; % Đáy nét đứt
				\draw[thick] (A) arc[start angle=180,end angle=360,x radius=2cm,y radius=0.5cm]
				(A) arc[start angle=180,end angle=360,x radius=2cm,y radius=0.6cm];
				% Vẽ các đoạn thẳng
				\draw[thick] 
				(-2,2)--(2,2)
				(K)--(L)
				(N)--(M)node[right]{$R=18$\,cm};
				\draw[dashed] 
				(J)--(I)--(C)
				; 
				\node[above right] at (L) {$L=20$\,cm};
				\foreach \x/\g in {A/180,C/0,B/120,I/120,O/90}{
					\draw[thick] (O)--(\x);
					\fill (\x) circle (1.2pt)+(\g:3mm)node{$\x$};}
				% Vẽ các mũi tên lực
				\draw[->,thick] (O)--($(A)!0.5!(O)$) node[pos=1,left] {$\overrightarrow{F}_1$};
				\draw[->,thick] (O)--($(B)!0.5!(O)$) node[pos=1,left] {$\overrightarrow{F}_2$};
				\draw[->,thick] (O)--($(C)!0.5!(O)$) node[pos=1,right] {$\overrightarrow{F}_3$};
				\draw[->,thick] (J)--(0,-2.5) node[pos=0.5,right] {$\overrightarrow{P}$};
				\pic[draw,"$\alpha$",angle radius=7mm,angle eccentricity=1.2]{angle=I--O--C};
