			\draw (P)--(M)--(N)--cycle;
			\draw (N)--(Q)--cycle;
			\draw (A)  node[shift={(-0.25,-0.25)}] {$A$};
			\draw (B)  node[shift={(-0.25,0.25)}] {$B$};
