\begin{ex}%[1D3H3-3]%[Dự án đề kiểm tra Toán 11 HKI NH24-25- Mui Doan]%[THPT-Nguyễn Quốc Trinh -Hà Nội]
	Cho hàm số $f(x)=\heva{&\dfrac{1}{4} x+\dfrac{1}{4} &\text{khi } x \leq 2\\&\dfrac{\sqrt{3x-2}-2}{x-2} &\text{khi } x > 2.}$
	\choiceTF
	{$\lim\limits_{x \to 2^{+}} f(x)=\dfrac{1}{2}$}
	{\True $\lim\limits_{x \to 0} f(x)=\dfrac{1}{4}$}
	{\True Hàm số $f(x)$ liên tục tại $x=2$}
	{\True $\lim\limits_{x \to 2^{-}} f(x)=\dfrac{3}{4}$}
	\loigiai{
		\begin{itemchoice}
			\itemch Ta có 
			\allowdisplaybreaks
			\begin{eqnarray*}
				\lim\limits_{x \to 2^{+}} f(x)&=&\lim\limits_{x \to 2^{+}}\dfrac{\sqrt{3x-2}-2}{x-2}
				\\
				&=&\lim\limits_{x \to 2^{+}}\dfrac{3x-2-4}{(x-2)\left(\sqrt{3x-2}+2\right)}
				\\
				&=&\lim\limits_{x \to 2^{+}}\dfrac{3(x-2)}{(x-2)\left(\sqrt{3x-2}+2\right)}
				\\
				&=&\lim\limits_{x \to 2^{+}}\dfrac{3}{\sqrt{3x-2}+2}
				\\
				&=&\dfrac{3}{4}.			
			\end{eqnarray*}
			\itemch Ta có $\lim\limits_{x \to 0} f(x)=\lim\limits_{x \to 0}\left(\dfrac{1}{4} x+\dfrac{1}{4}\right)=\dfrac{1}{4}$.
			\itemch Ta có $f(2)=\dfrac{1}{4}\cdot 2+\dfrac{1}{4}=\dfrac{3}{4}$.\\
			Ta có $\lim\limits_{x \to 2^{-}} f(x)=\lim\limits_{x \to 2^{-}}\left(\dfrac{1}{4} x+\dfrac{1}{4}\right)=\dfrac{3}{4}$.\\
			Suy ra $\lim\limits_{x \to 2^{-}} f(x)=\lim\limits_{x \to 2^{+}} f(x)=f(2)$.\\
			Do đó $f(x)$ liên tục tại $x=2$.
			\itemch Ta có $\lim\limits_{x \to 2^{-}} f(x)=\lim\limits_{x \to 2^{-}}\left(\dfrac{1}{4} x+\dfrac{1}{4}\right)=\dfrac{3}{4}$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[1D3N1-4]%[1D3N1-4]%[Dự án đề kiểm tra Toán 11 CHKI NH24-25- HOÀNG VŨ]%[THPT NGUYỄN QUỐC TRÌNH - TP. HÀ NỘI]
\begin{enumerate}
	\item Tìm $\lim\limits_{n\to+\infty} (3n^2+2n+5)$;
	\item Xét tính liên tục của hàm số $f(x)=\heva{&\dfrac{x^2+x-2}{x-1}\quad \text{khi}\ x\neq 1\\& 2x+1 \quad \quad \quad \text{khi} \ x=1}$ tại $x_0=1$.
\end{enumerate}
\loigiai{
\begin{enumerate}
	\item $\lim\limits_{n\to+\infty}(3n^2+2n+5)=\lim\limits_{n\to+\infty}n^2\left(3+\dfrac{2}{n}+\dfrac{5}{n^2}\right)=+\infty$.
	\item Ta có $f(1)=2\cdot1+1=3$.\\
	$\lim\limits_{x\to1}f(x)=\lim\limits_{x\to1}\dfrac{x^2+x-2}{x-1}=\lim\limits_{x\to1}\dfrac{(x-1)(x+2)}{x-1}=\lim\limits_{x\to1}(x+2)=3$.\\
	Vì $f(2)=\lim\limits_{x\to1}f(x)=3$ nên hàm số $f(x)$ liên tục tại $x_0=1$.
\end{enumerate}}
\end{ex}

