	Giới hạn $\lim\limits_{n \to+\infty}\left(\sqrt{n^2+4n}-n\right)$ bằng
	\choice
	{$4$}
	{$+\infty$}
	{\True $2$}
	{$0$}
	\loigiai{
		Ta có $\begin{aligned}[t]
			\lim\limits_{n \to+\infty}\left(\sqrt{n^2+4n}-n\right)&=\lim\limits_{n \to+\infty} \dfrac{\left(\sqrt{n^2+4n}\right)^2-n^2}{\sqrt{n^2+4n}+n}\\
			&=\lim\limits_{n \to+\infty} \dfrac{4n}{n\left(\sqrt{1+\dfrac{4}{n}}+1\right)}\\
			&=\lim\limits_{n \to+\infty} \dfrac{4}{\sqrt{1+\dfrac{4}{n}}+1}\\
			&=2.
		\end{aligned}$
	}
\end{ex}

