	Giới hạn $\lim\limits_{n \to+\infty}\left(2^n+3^n-4^n\right)$ bằng
	\choice
	{$1$}
	{$-4$}
	{$+\infty$}
	{\True $-\infty$}
	\loigiai{
		Ta có $\lim\limits_{n \to+\infty}\left(2^n+3^n-4^n\right)=\lim\limits_{n \to+\infty} 4^n\left(\left(\dfrac{2}{4}\right)^n+\left(\dfrac{3}{4}\right)^n-1\right)$.\\
		Vì $\lim\limits_{n \to+\infty} 4^n=+\infty$, $\lim\limits_{n \to+\infty} \left(\left(\dfrac{2}{4}\right)^n+\left(\dfrac{3}{4}\right)^n-1\right)=-1$.\\
		Nên $\lim\limits_{n \to+\infty}\left(2^n+3^n-4^n\right)=-\infty$.\\
	}
\end{ex}

