		\begin{itemchoice}
			\itemch Chia cả tử thức và mẫu thức cho $n^2$, ta được:\\
			$$\dfrac{n^2+4n-3}{5-2n}=\dfrac{1+\dfrac{4}{n}-\dfrac{3}{n^2}}{\dfrac{5}{n^2}- \dfrac{2}{n}},\forall n\in\mathbb{N^*}.$$
			%	\textbf{Đúng}.
			\itemch 
			Áp dụng các quy tắc tính giới hạn, ta có:
			$$\lim\limits_{n\to+\infty}{\left(1+\dfrac{4}{n}-\dfrac{3}{n^2}\right)}=1.$$
			%\textbf{Đúng}.
			\itemch
			Áp dụng các quy tắc tính giới hạn, ta có:
			$\lim\limits_{n\to+\infty}\left({\dfrac{5}{n^2}- \dfrac{2}{n}}\right)=0.$
			%\textbf{Đúng}.
			\itemch
			Áp dụng các quy tắc tính giới hạn, ta có:
			$$\lim\limits_{n\to+\infty}{\dfrac{n^2+4n-3}{5-2n}}=\lim\limits_{n\to+\infty}{\dfrac{1+\dfrac{4}{n}-\dfrac{3}{n^2}}{\dfrac{5}{n^2}- \dfrac{2}{n}}}.$$
			Vì $\lim\limits_{n\to+\infty}{\left(1+\dfrac{4}{n}-\dfrac{3}{n^2}\right)}=1$ và
			khi $n\to+\infty$ thì $5-2n<0$ nên
			$$\lim\limits_{n\to+\infty}{\dfrac{n^2+4n-3}{5-2n}}=\lim\limits_{n\to+\infty}{\dfrac{1+\dfrac{4}{n}-\dfrac{3}{n^2}}{\dfrac{5}{n^2}- \dfrac{2}{n}}}=-\infty$$
			%\textbf{Sai}.
		\end{itemchoice}
	}
\end{ex}

