\begin{ex}%[1D3H2-5]%[ĐỀ KIẾM TRA CUỐI HK1 - SỞ GD BẮC NINH]%[Nguyễn Ngọc Huy Trường]
	Tính các giới hạn sau
	\begin{multicols}{3}
		\begin{enumerate}
			\item $\displaystyle \lim\limits_{x \to -8} |2x - 1|$;
			\item $\displaystyle \lim\limits_{x \to 5} \dfrac{x^2 - 25}{5 - x}$;
			\item $\displaystyle \lim\limits_{x \to -\infty} \dfrac{\sqrt{4x^2 - x + 11}}{x + 2025}$.
		\end{enumerate}
	\end{multicols}
	\loigiai{
		\begin{enumerate}
			\item Ta có $\lim\limits_{x \to - 8} |2x - 1| = |2\cdot (-8)-1| = 17$.
			\item Ta có $\lim\limits_{x \to 5} \dfrac{x^2 - 25}{5-x} = \lim\limits_{x \to 5} \dfrac{(x-5)(x+5)}{-(x-5)} = \lim\limits_{x \to 5} (-x-5) = -5 -5 = -10$. 
			\item Ta có
			\allowdisplaybreaks
			\begin{eqnarray*}
				\lim\limits_{x \to -\infty} \dfrac{\sqrt{4x^2 - x + 11}}{x + 2025} &=& \lim\limits_{x \to -\infty} \dfrac{\sqrt{x^2 \left( 4 - \dfrac{1}{x} + \dfrac{11}{x^2} \right)}}{x + 2025} \\
				&=& \lim\limits_{x \to -\infty} \dfrac{|x| \sqrt{4 - \dfrac{1}{x} + \dfrac{11}{x^2}}}{x + 2025} \\
				&=& \lim\limits_{x \to -\infty} \dfrac{-x \cdot \sqrt{4 - \dfrac{1}{x} + \dfrac{11}{x^2}}}{x \left( 1 + \dfrac{2025}{x} \right)} \\
				&=& \lim\limits_{x \to -\infty} \dfrac{-\sqrt{4 - \dfrac{1}{x} + \dfrac{11}{x^2}}}{1 + \dfrac{2025}{x}} \\
				&=& \dfrac{-\sqrt{4}-0+0}{1+0} \\
				&=& -2.
			\end{eqnarray*}
		\end{enumerate}
	}
\end{ex}

\begin{ex}%[1H4H4-2]%[ĐỀ KIẾM TRA CUỐI HK1 - SỞ GD BẮC NINH]%[Nguyễn Ngọc Huy Trường]
	Cho tứ diện $ABCD$. Gọi $M$, $N$, $P$ lần lượt là trung điểm của các cạnh $CD$, $BD$, $AD$.
	\begin{enumerate}
		\item Chứng minh rằng mặt phẳng $(MNP)$ song song với mặt phẳng $(ABC)$.
		\item Gọi $H$ là trung điểm của đoạn thẳng $AN$. Tìm giao điểm $E$ của đường thẳng $BH$ và mặt phẳng $(MNP)$.
		\item Gọi $G$ là giao điểm của $BE$ và $AD$. Chứng minh rằng $G$ là trọng tâm của tam giác $AEN$.
	\end{enumerate}
	\loigiai{
		\begin{center}
			\begin{tikzpicture}[scale=1, font=\footnotesize,line join=round, line cap=round, >=stealth]
				\path 
				(0,0) coordinate (B)
				++(-60:2) coordinate (C)
				(4,0) coordinate (D)
				($(B)+(60:3)$) coordinate (A)
				($(C)!1/2!(D)$) coordinate (M)
				($(B)!1/2!(D)$) coordinate (N)
				($(A)!1/2!(D)$) coordinate (P)
				($(A)!1/2!(N)$) coordinate (H)
				($(B)!2!(H)$) coordinate (E)
				(intersection of H--E and A--D) coordinate (G)
				;
				\foreach \i in{B,C,D,E} {\draw (A)--(\i);};
				\draw (B)--(C)--(D) (M)--(P) (G)--(E)--(P);
				\draw[dashed] (G)--(B)--(D) (A)--(N)--(M) (N)--(P);
				\foreach \i/\g in {A/90,B/180,C/-90,D/0,M/-90,N/-120,P/0,H/-110,E/30,G/90}
				\fill[black] (\i) circle(1pt)+(\g:3mm)node[scale=1]{$\i$};
			\end{tikzpicture}
		\end{center}
		\begin{enumerate}
			\item Theo tính chất đường trung bình của tam giác, ta có $MP \parallel AC$. \\
			Ta có $\heva{&MP \parallel AC\\&AC \subset (ABC) \\&MP \not\subset (ABC)}$ suy ra $MP \parallel (ABC)$. \\
			Tương tự, ta chứng minh được $MN \parallel (ABC)$. \\
			Ta có $\heva{&MP \parallel (ABC) \\ &MN \parallel (ABC)\\&\text{Trong } (MNP),MP\cap MN=M}$ suy ra $(MNP) \parallel (ABC)$. \\
			Vậy $(MNP) \parallel (ABC)$.
			\item Trong mặt phẳng $(ABD)$, ta có $NP \parallel AB$ và $BH$ cắt $AB$ nên $BH$ cắt $NP$. \\
			Gọi $E$ là giao điểm của hai đường thẳng $BH$ và $NP$. \\
			Ta có $\heva{&E \in BH \\&E \in NP \subset (MNP)}$ suy ra $E = BH \cap (MNP)$. \\
			Vậy $E = BH \cap (MNP)$.
			\item Ta thấy $NE \parallel AB$. Vì $\widehat{BAH} = \widehat{ENH}$ (hai góc so le trong), $HA = HN$ ($H$ là trung điểm của đoạn thẳng $AN$), $\widehat{AHB} = \widehat{NHE}$ (hai góc đối đỉnh) nên $\triangle BAH = \triangle ENH$. \\
			Suy ra $AB = EN$. Mà $NP = \dfrac{1}{2}AB$ nên $NP = \dfrac{1}{2}EN$ dẫn đến $P$ là trung điểm của $EN$. \\
			Trong tam giác $AEN$, ta có hai đường trung tuyến $AP$, $EH$ cắt nhau tại $G$. \\
			Vậy $G$ là trọng tâm của tam giác $AEN$.
		\end{enumerate}
	}
\end{ex}

