				(0,0) coordinate (A)
				(ad,0) coordinate (D)
				(gocA:ab) coordinate (B)
				($(D)-(A)+(B)$) coordinate (C)
				(gs:sa) coordinate (S)
				(intersection of A--C and B--D) coordinate (O)
				($(S)!2/3!(O)$) coordinate (G)
				;
				\draw (S)--(B)--(C)--(D)--(S)--(C);
				\draw[dashed] (S)--(A)--(D) (B)--(A) (S)--(O)
				(A)--(C) (B)--(D)
