				\foreach \x/\goc in {S/90,A/240,C/-60,B/-90,M/-40, G/-90,G'/30}
				\draw[fill=white] (\x) node[shift={(\goc:7pt)},font=\scriptsize]{$\x$} circle (1pt);
				\draw[->,>=stealth] (A)--(x)node[below]{$x$};
