\begin{ex}%[2H2H1-3]%[Dự án A-HKI NH24-25-Đợt 6-Nguyễn Hoàng Việt]%[THPT Duy Tân - Tỉnh Kon Tum]
	\immini{Cho hình lập phương $ABCD.A'B'C'D'.$
		\choiceTF
		{Gọi $G$ là trọng tâm $\triangle ABC$ thì $\overrightarrow{A'A}+\overrightarrow{A'B}+\overrightarrow{A'C}=\overrightarrow{A'G}$}
		{\True $\overrightarrow{BA}+\overrightarrow{BC}+\overrightarrow{BB'}=\overrightarrow{BD'}$}
		{\True $\overrightarrow{AB}+\overrightarrow{AD}=\overrightarrow{AC}$}
		{\True Nếu cạnh của hình lập phương là $a$ thì $\overrightarrow{AB}\cdot\overrightarrow{AC}=a^2$}}
	{\begin{tikzpicture}[declare function={a=5; b=0.45*a; h=0.65*a;}, line join=round, line cap=round, >=stealth, scale=0.6]
		\path (0:0) coordinate (A)
		(220:b) coordinate (B)
		($(B)+(a,0)$) coordinate (C)
		(a,0) coordinate (D)
		\foreach \x in {A,B,C,D}{(\x)++(90:h) coordinate (\x1)};
		\draw[dashed] (A1)--(A)--(B) (A)--(D);
		\draw (B1)--(A1)--(D1)--(C1) (D1)--(D)--(C) (B)--(C)--(C1)--(B1)--cycle;
		\foreach \x/\goc/\t in {A/45/A,B/-90/B,C/-90/C,D/0/D,A1/90/A',B1/90/B',C1/90/C',D1/90/D'}
		\fill (\x) circle (1pt) node[shift={(\goc:7pt)},font = \footnotesize]{$\t$};
		\end{tikzpicture}}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}. 
			%		\allowdisplaybreaks
			\begin{eqnarray*}
				G \text{ là trọng tâm }  \triangle ABC
				&\Rightarrow & \overrightarrow{GA}+\overrightarrow{GB}+\overrightarrow{GC}=\overrightarrow{0}\\
				&\Rightarrow & \forall A': \overrightarrow{A'A}+\overrightarrow{A'B}+\overrightarrow{A'C}=3\overrightarrow{A'G}.\end{eqnarray*}
			Do đó $\overrightarrow{A'A}+\overrightarrow{A'B}+\overrightarrow{A'C}=\overrightarrow{A'G}$ là khẳng định sai.

			\itemch \textbf{Đúng}. Theo quy tắc hình hộp, ta có $\overrightarrow{BA}+\overrightarrow{BC}+\overrightarrow{BB'}=\overrightarrow{BD'}.$
			\itemch \textbf{Đúng}. Theo quy tắc hình bình hành, ta có $\overrightarrow{AB}+\overrightarrow{AD}=\overrightarrow{AC}.$
			\itemch \textbf{Đúng}. Ta có 
			\begin{eqnarray*}
				\overrightarrow{AB}\cdot\overrightarrow{AC}
				&= & 	\overrightarrow{AB}\cdot\left(\overrightarrow{AB}+\overrightarrow{AD}\right)\\
				&= & {\overrightarrow{AB}}^2+\overrightarrow{AB}\cdot\overrightarrow{AD}\\
				&= & AB^2 = a^2.
			\end{eqnarray*}
		\end{itemchoice}
	}
