\begin{ex}%[2D1V4-3]%[Dự án đề kiểm tra Toán 12 HK1 NH24-25- Nhật Thiện]%[THPT Mạc Đĩnh Chi - TP HCM]
	Cho hàm số $y=\dfrac{x^2+x-1}{x-1}$.
	\choiceTF
	{\True Đường tiệm cận đứng của đồ thị hàm số có phương trình $x=1$}
	{Giá trị cực tiểu của hàm số bằng $1$}
	{Khoảng cách giữa hai điểm cực trị của đồ thị hàm số bằng $\sqrt{26}$}
	{\True Gọi $M$ là điểm thuộc đồ thị hàm số sao cho $M$ cách đều hai đường tiệm cận của đồ thị hàm số và $I$ là tâm đối xứng của đồ thị hàm số. Ta có $IM=\sqrt{2+2\sqrt{2}}$}
	\loigiai{
		\begin{itemchoice}
			\itemch Ta có $\lim\limits_{x\to 1^+} y=+\infty$ và $\lim\limits_{x\to 1^-} y=-\infty$.\\
			Suy ra đồ thị hàm số có đường tiệm cận đứng là $x=1$.
			\itemch Xét $y'=\dfrac{(2x+1)(x-1)-(x^2+x-1)}{(x-1)^2}=\dfrac{x^2-2x}{(x-1)^2}$.\\
			Cho $y'=0\Leftrightarrow \hoac{&x=0\\&x=2.}$\\
			Bảng biến thiên của hàm số
			\begin{center}
				\begin{tikzpicture}
					\tkzTabInit[nocadre=false,lgt=1.2,espcl=2.5,deltacl=0.6]
					{$x$ /0.6,$y’$ /0.6,$y$ /2}
					{$-\infty$,$0$,$1$,$2$,$+\infty$}
					\tkzTabLine{,+,0,-,d,-,0,+,}
					\tkzTabVar{-/$-\infty$,+/$1$,-D+/$-\infty$/$+\infty$,-/$5$,+/$+\infty$}
				\end{tikzpicture}
			\end{center}
			Dựa vào bảng biến thiên, giá trị cực tiểu của hàm số bằng $5$.
			\itemch Hai điểm cực trị của đồ thị hàm số lần lượt là $(0;1)$ và $(2;5)$.\\
			Khoảng cách giữa hai điểm cực trị của đồ thị hàm số bằng
			$$\sqrt{(2-0)^2+(5-1)^2}=2\sqrt{5}.$$
			\itemch Ta có $y=x+2+\dfrac{1}{x-1}$.\\
			Mà $\lim\limits_{x\to +\infty} \left[y-(x+2)\right]=0$.\\
			Nên đường thẳng $y=x+2$ là đường tiệm cận xiên của đồ thị hàm số.\\
			Điểm $I$ là tâm đối xứng của đồ thị hàm số nên $I$ là giao điểm của đường tiệm cận đứng và tiệm cận xiên.\\
			Khi đó, ta được $I(1;3)$.\\
			Gọi $M\left(x;x+2+\dfrac{1}{x-1}\right)$ thuộc đồ thị hàm số.\\
			Khoảng cách từ điểm $M$ đến các đường tiệm cận bằng nhau nên
			\begin{eqnarray*}
				& &|x-1|=\left|\dfrac{1}{x-1}\right|\cdot \dfrac{1}{\sqrt{2}}\\
				&\Rightarrow& (x-1)^2=\dfrac{1}{\sqrt{2}}
			\end{eqnarray*}
			Ta có
			\begin{eqnarray*}
				IM&=&\sqrt{(x-1)^2+\left(x-1+\dfrac{1}{x-1}\right)^2}=\sqrt{(x-1)^2+(x-1)^2+\dfrac{1}{(x-1)^2}+2}\\
				&=&\sqrt{\dfrac{1}{\sqrt{2}}+\dfrac{1}{\sqrt{2}}+\sqrt{2}+2}=\sqrt{2+2\sqrt{2}}.
			\end{eqnarray*}
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2H2V2-4]%[Dự án đề kiểm tra Toán 12 HK1 NH24-25- Nhật Thiện]%[THPT Mạc Đĩnh Chi - TP HCM]
	Trong không gian $Oxyz$, cho ba điểm $A(2;-1; 1)$, $B(-3; 1; 2)$, $C(1; 2;-3)$.
	\choiceTF
	{\True Trung điểm của đoạn thẳng $AB$ là điểm $I\left(-\dfrac{1}{2}; 0; \dfrac{3}{2}\right)$}
	{Tam giác $ABC$ là tam giác cân}
	{\True Trong tam giác $ABC$, góc $A$ là góc nhọn}
	{\True Điểm $M$ di động trên mặt phẳng $(Oyz)$, giá trị lớn nhất của biểu thức $MA^2-2MB^2$ bằng $-4$}
	\loigiai{
		\begin{itemchoice}
			\itemch Trung điểm của đoạn $AB$ là điểm $I\left(-\dfrac{1}{2};0;\dfrac{3}{2}\right)$.
			\itemch Ta có $\overrightarrow{AB}=(-5;2;1)$, $\overrightarrow{AC}=(-1;3;-4)$ và $\overrightarrow{BC}=(4;1;-5)$.\\
			Suy ra $AB=\sqrt{30}$, $AC=\sqrt{26}$ và $BC=\sqrt{42}$.\\
			Do đó, tam giác $ABC$ không là tam giác cân.
			\itemch Ta có
			$\cos \widehat{A}=\cos \left(\overrightarrow{AB},\overrightarrow{AC}\right)=\dfrac{\overrightarrow{AB}\cdot \overrightarrow{AC}}{AB\cdot AC}=\dfrac{(-5)\cdot (-1)+2\cdot 3+1\cdot (-4)}{\sqrt{30}\cdot \sqrt{26}}=\dfrac{7}{2\sqrt{195}}$.\\
			Vậy góc $A$ là góc nhọn.
			\itemch Gọi $E$ là điểm thỏa mãn $\overrightarrow{EA}-2\overrightarrow{EB}=\overrightarrow{0}$.\\
			Suy ra $E(-8;3;3)$.\\
			Xét \begin{eqnarray*}
				MA^2-2MB^2&=&\overrightarrow{MA}^2-2\overrightarrow{MB}^2=\left(\overrightarrow{EA}-\overrightarrow{EM}\right)^2-2\left(\overrightarrow{EB}-\overrightarrow{EM}\right)^2\\
				&=&\overrightarrow{EA}^2-2\overrightarrow{EB}^2-\overrightarrow{EM}^2-2\overrightarrow{EM}\cdot \left(\overrightarrow{EA}-2\overrightarrow{EB}\right)=\overrightarrow{EA}^2-2\overrightarrow{EB}^2-\overrightarrow{EM}^2\\
				&=& EA^2-2EB^2-EM^2.
			\end{eqnarray*}
			Biểu thức $MA^2-2MB^2$ đạt giá trị lớn nhất khi $EM$ nhỏ nhất.\\
			Suy ra $M$ là hình chiếu của $E$ lên $(Oyz)$ hay $M(0;3;3)$.\\
			Do đó $EA=\sqrt{120}$, $EB=\sqrt{30}$, $EM=8$.\\
			Khi đó $MA^2-2MB^2=EA^2-2EB^2-EM^2=120-2\cdot 30- 8^2=-4$.
		\end{itemchoice}
	}
\end{ex}

