			%Vẽ đồ thị 
			\draw[smooth, blue,thick] plot[domain=\xmin:-2.25](\x,{((\x)+3)/((\x)+2)});
			\draw[smooth, blue,thick] plot[domain=-1.71:\xmax](\x,{((\x)+3)/((\x)+2)});
			%Đánh số
			\foreach \x in 	{-6,-5,-4,-3,-2,-1,1,2}
			\draw[shift={(\x,0)},color=black,thick] (0pt,2pt) -- (0pt,-2pt) node[below]{$\x$};
			\foreach \y in 	{1,2,3}
			\draw[shift={(0,\y)},color=black,thick] (2pt,0pt) -- (-2pt,0pt) node[above right]{$\y$};
			\foreach \y in 	{-1,-2}
			\draw[shift={(0,\y)},color=black,thick] (2pt,0pt) -- (-2pt,0pt) node[below right]{$\y$};
		\end{tikzpicture}
	\end{center}
	Tính giá trị $M=a+b+c$.
	\choice
	{$-1$}
	{\True $4$}
	{$0$}
	{$3$}
	\loigiai{
		Theo hình vẽ, đồ thị hàm số $y=\dfrac{ax+3}{bx+c}$ đi qua điểm $A(-3;0)$ nên $$0=\dfrac{-3a+3}{-3b+c} \Rightarrow -3a+3=0 \Rightarrow a=1. \quad (1)$$
		Đồ thị của hàm số $y=\dfrac{ax+3}{bx+c}$ có đường tiệm cận 
		đứng là $x=-\dfrac{c}{b}$ và đường tiệm cận ngang là $y=\dfrac{a}{b}$.\\
		Mà theo hình vẽ, đồ thị của hàm số $y=\dfrac{ax+3}{bx+c}$ có đường tiệm cận đứng là $x=-2$ và đường tiệm cận ngang là $y=1$
		nên ta có $\heva{&-\dfrac{c}{b}=-2 \quad (2) \\& \dfrac{a}{b}=1 \quad (3).}$\\
		Từ (1), (2) và (3) ta có hệ
		$$\heva{&a=1\\&-\dfrac{c}{b}=-2 \\& \dfrac{a}{b}=1 } \Rightarrow \heva{&a=1\\&2b-c=0 \\& a-b=0} \Rightarrow \heva{&a=1\\&b=1\\&c=2.}$$
		Vậy $M=a+b+c=1+1+2=4$.
	}
\end{ex}

