\begin{ex}%[2D1H3-6]%[Dự án đề kiểm tra Toán 12 HKI NH24-25-Nguyễn Hữu Duy]%[THPT Gia Định - TPHCM]
	Công ty $X$ chuyên sản xuất một loại sản phẩm, bộ phận sản xuất ước tính rằng với $q$ sản phẩm được sản xuất trong một tháng thì tổng chi phí sẽ là $C(q)=8q^2+40 q+1\,300$ (nghìn đồng) và mỗi sản phẩm công ty bán với giá $P(q)=1\,400-2q$ (nghìn đồng).
	\choiceTF
	{Chi phí mỗi tháng công ty phải bỏ ra để sản xuất $50$ sản phẩm là $23\,400$ (nghìn đồng)}
	{\True Lợi nhuận bán được $q$ sản phẩm là $F(q)=-10q^2+1\,360q-1\,300$ (nghìn đồng)}
	{\True Lợi nhuận cao nhất trong một tháng của công ty là hơn $44\,000$ (nghìn đồng)}
	{Nếu số lượng sản phẩm bán ra trong một tháng nằm trong khoảng từ $60$ đến $70$ thì lợi nhuận sẽ được ước tính trong khoảng $44\,200$ đến $44\,840$ (nghìn đồng)}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}.\\
			Chi phí mỗi tháng công ty phải bỏ ra để sản xuất $50$ sản phẩm là
			\[C(50)=8\cdot 50^2+40\cdot 50+1\,300=23\,300 (\text{ nghìn đồng}).\]
			\itemch \textbf{Đúng}.\\
			Doanh thu khi bán $q$ sản phẩm là 
			\[R(q)=q\cdot P(q)=q\cdot (1\,400-2q)=1\,400-2q^2.\]
			Lợi nhuận khi bán được $q$ sản phẩm là 
			\begin{align*}
				F(q)=R(q)-C(q)&=(1\,400-2q^2)-(8q^2+40 q+1\,300)\\
				&=-10q^2+1\,360q-1\,300  (\text{ nghìn đồng}).
			\end{align*}
			\itemch \textbf{Đúng}.\\
			Ta có $F(q)$ là hàm số bậc hai có hệ số $a=-10<0$ nên đạt giá trị lớn nhất tại \[q=-\dfrac{b}{2a}=-\dfrac{1\,360}{2\cdot (-10)}=68.\]
			Khi đó $F(68)=-10\cdot 68^2+1\,360\cdot 68-1\,300=44\,940$ (nghìn đồng).
			\itemch \textbf{Sai}.\\
			Với $q=60$ ta có $F(60)=-10\cdot 60^2+1\,360\cdot 60-1\,300=44\,300$ (nghìn đồng).\\
			Với $q=60$ ta có $F(70)=-10\cdot 70^2+1\,360\cdot 70-1\,300=44\,900$ (nghìn đồng).\\
			Vậy nếu số lượng sản phẩm bán ra trong một tháng nằm trong khoảng từ $60$ đến $70$ thì lợi nhuận sẽ được ước tính trong khoảng $44\,300$ đến $44\,900$ (nghìn đồng).
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D1H2-1]%[Dự án đề kiểm tra Toán 12 HKI NH24-25-Nguyễn Hữu Duy]%[THPT Gia Định - TPHCM]
	Cho hàm số $y=\dfrac{x^2-2x+2}{x-1}$.
	\choiceTF
	{\True Đạo hàm của hàm số đã cho là $y'=\dfrac{x^2-2x}{(x-1)^2}$}
	{Giá trị cực đại của hàm số bằng $2$}
	{Điểm cực tiểu của hàm số bằng $0$}
	{Đường thẳng đi qua hai điểm cực trị của đồ thị hàm số đã cho có phương trình là $y=2x+2$}
	\loigiai{
		Điều kiện xác định $x\neq 1$, suy ra tập xác định $\mathscr{D}=\mathbb{R}\setminus \{1\}$.
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Ta có \begin{eqnarray*}
				y' &=&\dfrac{\left(x^2-2x+2\right)'\cdot (x-1)-(x-1)'\cdot \left(x^2-2x+2\right)}{x-1)^2}\\
				&=& \dfrac{(2x-2)(x-1)-(x^2-2x+2)}{(x-1)^2}\\
				&=& \dfrac{x^2-2x}{(x-1)^2}.
			\end{eqnarray*}
			\itemch \textbf{Sai}.\\
			Xét $y'=0\Leftrightarrow x^2-2x=0\Leftrightarrow\hoac{&x=0 \\&x=2.}$\\
			Bảng biến thiên
			\begin{center}
				\begin{tikzpicture}
					\tkzTabInit[nocadre=false,lgt=1.2,espcl=2.5,deltacl=0.6]
					{$x$/0.6,$y'$/0.6,$y$/2}
					{$-\infty$,$0$,$1$,$2$,$+\infty$}
					\tkzTabLine{,+,$0$,-,d,-,$0$,+,}
					\tkzTabVar{-/$-\infty$,+/$-2$,-D+/$-\infty$/$+\infty$,-/$2$,+/$+\infty$}
				\end{tikzpicture}
			\end{center} 
			Từ bảng biến thiên, ta thấy giá trị cực đại của hàm số bằng $-2$.
			\itemch \textbf{Sai}.\\
			Từ bảng biến thiên, ta thấy điểm cực tiểu của hàm số bằng $2$.
			\itemch \textbf{Sai}.\\
			Gọi đường thẳng đi qua hai điểm cực trị đã cho có phương trình là $d\colon y=ax+b$.\\
			Do $d$ đi qua hai điểm $(0;-2)$ và $(2;2)$ nên ta có hệ phương trình
			\begin{align*}
				\heva{&b=-2 \\&2a+b=2}\Leftrightarrow \heva{&b=-2 \\&a=2.}
			\end{align*}
			Vậy phương trình đường thẳng cần tìm là $y=2x-2$.
		\end{itemchoice}
	}
\end{ex}

