\begin{ex}%[2H2H2-4]%[2-thpt-nguyen-du-br-vt-HKI-N24-251]%[Hiếu Mai] %Câu 1
	Trong không gian $O x y z$, cho $A(2 ; 0 ;-1)$, $B(1 ; 2 ; 1)$, $C(3 ; 1 ;-2)$.
	\choiceTF
	{\True Ba điểm $A$, $B$, $C$ lập được thành một tam giác}
	{$G(2 ; 1 ;-1)$ là trọng tâm của $\triangle ABC$}
	{Gọi $D(a ; b ; c)$ sao cho $ABCD$ là hình bình hành. Khi đó $ a+b+c= 5$}
	{\True $\cos \widehat{BAC}=-\dfrac{\sqrt{3}}{9}$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}. Ta thấy $\overrightarrow{AB} = (-1;2;2)$ và $\overrightarrow{AC} = (1;1;-1)$. Do $\dfrac{-1}{1} \ne \dfrac{2}{1}$ nên $\overrightarrow{AB}$ không cùng phương $\overrightarrow{AC}$.\\
			Suy ra ba điểm $A$, $B$, $C$ lập được thành một tam giác.

			\itemch \textbf{Sai}. $G\left(2 ; 1 ;-\dfrac{2}{3}\right)$ là trọng tâm của $\triangle ABC$.
			\itemch Ta có $\overrightarrow{CD} = (a-3;b-1;c+2)$ và $\overrightarrow{BA} = (1;-2;-2)$.\\
			Để $ABCD$ là hình bình hành thì $\overrightarrow{CD} = \overrightarrow{BA}$ hay $a=4$, $b=-1$ và $c=-4$.\\
			Khi đó, $a+b+c=-1$.

			\itemch \textbf{Đúng}. Ta có
			$$
			\cos\widehat{BAC}
			= \cos(\overrightarrow{AB},\overrightarrow{AC})
			= \dfrac{\overrightarrow{AB} \cdot \overrightarrow{AC}}{AB \cdot AC}
			=-\dfrac{\sqrt{3}}{9}.
			$$
		\end{itemchoice}
	}
