	Cho hàm số $y=f(x)=\log_{\tfrac{1}{2}}(x+1)$.
	\choiceTF
	{Tập xác định của hàm số là $\mathscr{D} = (0; +\infty)$}
	{Hàm số đồng biến trên khoảng $(-1; +\infty)$}
	{\True Đồ thị hàm số đi qua điểm $M\left(-\dfrac{3}{4}; 2\right)$}
	{\True Tổng giá trị lớn nhất và giá trị nhỏ nhất của hàm số $y=f(x)$ trên đoạn $\left[-\dfrac{1}{2};3\right]$ là $-1$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai.} Điều kiện xác định $x+1>0\Leftrightarrow x > -1$. \\
			Vậy tập xác định của hàm số là $\mathscr{D} = (-1;+\infty)$.
			\itemch \textbf{Sai.} Do $\dfrac{1}{2} < 1$ nên hàm số $y=\log_{\tfrac{1}{2}}(x+1)$ nghịch biến trên $(-1;+\infty)$.
			\itemch \textbf{Đúng.} Thay $x=-\dfrac{3}{4}$ vào $y=\log_{\tfrac{1}{2}}(x+1)$, ta được
			$$y=\log_{\tfrac{1}{2}}\left(-\dfrac{3}{4}+1\right)=2.$$
			Vậy đồ thị hàm số đi qua điểm $M\left(-\dfrac{3}{4}; 2\right)$.
			\itemch \textbf{Đúng.} Ta có 
			\allowdisplaybreaks
			\begin{eqnarray*}
				&&-\dfrac{1}{2}\le x \le 3 \\
				&\Leftrightarrow& \dfrac{1}{2}\le x+1\le 4 \\
				&\Leftrightarrow& \log_{\tfrac{1}{2}}4 \le \log_{\tfrac{1}{2}}(x+1)\le \log_{\tfrac{1}{2}}\dfrac{1}{2} \\
				&\Leftrightarrow& -2 \le y \le 1.
			\end{eqnarray*}
			Suy ra giá trị lớn nhất và nhỏ nhất của hàm số $y=f(x)$ trên đoạn $\left[-\dfrac{1}{2};3\right]$ lần lượt là $1$ và $-2$. \\
			Vậy tổng giá trị lớn nhất và giá trị nhỏ nhất của hàm số $y=f(x)$ trên đoạn $\left[-\dfrac{1}{2};3\right]$ là $1-2=-1$.
		\end{itemchoice}
	}
\end{ex}

