				\draw[shift={(0,3)}] (2pt,0pt)--(-2pt,0pt) node[left]{$600$};
				\draw[shift={(0,4)}] (2pt,0pt)--(-2pt,0pt) node[left]{$800$};
				\draw[shift={(0,5)}] (2pt,0pt)--(-2pt,0pt) node[left]{$1000$};
