	\draw[smooth, line width=0.5] plot[domain= -3.5:1.5] (\x,{-1* (\x)^(2)-2*(\x)+3}); 
	\draw (0,4) node[right]{$4$} (-1,0) node[below]{$-1$};
	\draw (0,3) node[right]{$3$} (-2,0) node[below]{$-2$};
