				(-1.6,3.5)node{$x+y=30$}
				(0,3) node[shift=(45:0.25)]{$A$}
				(2,1) circle(1pt) node[shift=(45:0.25)]{$B$}
				(2.5,0) circle(1pt) node[shift=(45:0.25)]{$C$};
				\foreach \x in {-20,-10,10,20,30,40} {
