\begin{ex}%[0H5V4-3]%[Dự án đề kiểm tra Toán 10 HKI NH24-25- Nguyễn Ngọc Duy]%[THPT - Tỉnh Bắc Giang]
	Cho tứ giác lồi $ABCD$, hai đường chéo $AC$ và $BD$ cắt nhau tại điểm $O$. Gọi điểm $H$, $K$ lần lượt là trực tâm của các tam giác $ABO$ và $CDO$. Gọi điểm $I$, $J$ lần lượt là trung điểm của cạnh $AD$ và $BC$. Chứng minh rằng $HK \perp IJ$.
	\loigiai{
		\begin{center}
				\begin{tikzpicture}[line join=round, line cap=round, >=stealth, scale=1]
				\path
				(1,3.5) coordinate (C)
				(5,4) coordinate (B)
				(7,0) coordinate (A)
				(0,0) coordinate (D);
				\path ($(A)!0.5!(D)$) coordinate (I);
				\path ($(B)!0.5!(C)$) coordinate (J);
				\path (intersection of C--A and B--D) coordinate (O);
				\path ($(B)!(A)!(O)$) coordinate (AH)
				($(A)!(B)!(O)$) coordinate (BH)
				(intersection of A--AH and B--BH) coordinate (H);
				\path ($(D)!(C)!(O)$) coordinate (CH)
				($(C)!(D)!(O)$) coordinate (DH)
				(intersection of C--CH and D--DH) coordinate (K);
				\draw (C)--(B)--(A)--(D)--(C)--(A) (B)--(D) (I)--(J) (D)--(DH) (C)--(CH) (A)--(AH) (B)--(BH) (H)--(K);

				\foreach \x/\y/\z in {C/CH/D,D/DH/O,A/AH/B,A/BH/B}{
					\path (\y) pic[draw,angle radius=5pt]{right angle = \x--\y--\z};
				}
				\foreach \t/\g in {C/120,B/45,A/-30,D/210,O/-90,I/-90,J/90,H/-90,K/-90}{
					\fill (\t) circle (1pt) node[shift={(\g:9pt)}]{$ \t $};
				}
			\end{tikzpicture}
		\end{center}
		Ta có $\overrightarrow{IJ} = \overrightarrow{IA} + \overrightarrow{AC} + \overrightarrow{CJ}$ và $\overrightarrow{IJ} = \overrightarrow{ID} + \overrightarrow{DB} + \overrightarrow{BJ}$.\\
		Mặt khác $I$, $J$ là trung điểm $AD$, $BC$.\\
		Do đó $2\overrightarrow{IJ}=\left(\overrightarrow{IA}+\overrightarrow{ID}\right)+\left(\overrightarrow{CJ}+\overrightarrow{BJ}\right)+\overrightarrow{AC}+\overrightarrow{BD}=\overrightarrow{AC}+\overrightarrow{BD}$.\\
		Ta có
		\begin{eqnarray*}
			&&\overrightarrow{HK} \cdot 2\overrightarrow{IJ} = \overrightarrow{HK} \cdot (\overrightarrow{AC} + \overrightarrow{DB})\\
			&=& \overrightarrow{HK} \cdot \overrightarrow{AC} + \overrightarrow{HK} \cdot \overrightarrow{DB}\\
			&=& \left(\overrightarrow{HB} + \overrightarrow{BD} + \overrightarrow{DK}\right) \cdot \overrightarrow{AC} + \left(\overrightarrow{HA} + \overrightarrow{AC} + \overrightarrow{CK}\right) \cdot \overrightarrow{DB}\\
			&=& \overrightarrow{HB}\cdot\overrightarrow{AC}
			+\overrightarrow{BD}\cdot\overrightarrow{AC}
			+\overrightarrow{DK}\cdot\overrightarrow{AC}
			+\overrightarrow{HA}\cdot\overrightarrow{DB}
			+\overrightarrow{AC}\cdot\overrightarrow{DB}
			+\overrightarrow{CK}\cdot\overrightarrow{DB}.
		\end{eqnarray*}
		Vì $H$ là trực tâm của $\triangle ABO$ nên ta có $\overrightarrow{HB}\cdot\overrightarrow{AC}=\overrightarrow{0}$, $\overrightarrow{HA}\cdot\overrightarrow{DB}=\overrightarrow{0}$.\\
		Vì $K$ là trực tâm của $\triangle CDO$ nên ta có $\overrightarrow{DK}\cdot\overrightarrow{AC}=\overrightarrow{0}$, $\overrightarrow{CK}\cdot\overrightarrow{DB}=\overrightarrow{0}$.\\
		Do đó $\overrightarrow{HK} \cdot 2\overrightarrow{IJ} =  \overrightarrow{AC} \cdot \left(\overrightarrow{BD} + \overrightarrow{DB}\right) = \overrightarrow{AC} \cdot \overrightarrow{0} = 0$.\\
		Vậy $\overrightarrow{HK} \cdot \overrightarrow{IJ} = 0$ nên $HK \perp IJ$.
	}
\end{ex}

