\begin{ex}%[Dự án đề kiểm tra NH24-25 -đợt 7 - Nhật Thiện]%[THPT Thị Xã Quảng Trị  - Quảng Trị]%[0H5H4-1]
	Cho hình chữ nhật $ABCD$ tâm $O$ có $AB=4$; $BC=3$. 
	\choiceTF
	{$\left|\overrightarrow{AB}+\overrightarrow{BC}\right|=7$}
	{\True $\left|\overrightarrow{DA}+\overrightarrow{DC}\right|=5$}
	{\True $\overrightarrow{MA}+\overrightarrow{MB}+\overrightarrow{MC}+\overrightarrow{MD}=4\overrightarrow{MO}$, với $M$ là điểm bất kỳ}
	{\True $\overrightarrow{AB}\cdot \overrightarrow{AC}=16$}
	\loigiai{
		\begin{center}
			\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
				\path 
				(0,3) coordinate (A)
				(0,0) coordinate (B)
				(2,0) coordinate (C)
				($(A)!.5!(C)$) coordinate (O)
				($(A)+(C)-(B)$) coordinate (D)
				;

				\draw (A)--(B)--(C)--(D)--cycle (A)--(C) (B)--(D);

				\foreach \p/\r in {A/90,B/-120,C/-60,D/60,O/180}
				\fill (\p) circle (1.5pt) node[shift={(\r:3mm)}]{$\p$};
			\end{tikzpicture}
		\end{center}
		\begin{itemchoice}
			\itemch  $\left|\overrightarrow{AB}+\overrightarrow{BC}\right|=\left|\overrightarrow{AC}\right|=AC=\sqrt{BC^2+AB^2}=5$.
			\itemch $\left|\overrightarrow{DA}+\overrightarrow{DC}\right|=\left|\overrightarrow{DB}\right|=DB=AC=5$.
			\itemch Do $O$ là tâm của hình chữ nhật $ABCD$ nên $\heva{&\overrightarrow{OA}+\overrightarrow{OC}=\overrightarrow{0}\\&\overrightarrow{OB}+\overrightarrow{OD}=\overrightarrow{0}.}$\\
			Khi đó 
			\begin{eqnarray*}
				& &\overrightarrow{OA}+\overrightarrow{OC}+\overrightarrow{OB}+\overrightarrow{OD}=\overrightarrow{0}\\
				&\Leftrightarrow& \overrightarrow{MA}-\overrightarrow{MO}+\overrightarrow{MC}-\overrightarrow{MO}+\overrightarrow{MB}-\overrightarrow{MO}+\overrightarrow{MD}-\overrightarrow{MO}=\overrightarrow{0}\\
				&\Leftrightarrow& \overrightarrow{MA}+\overrightarrow{MB}+\overrightarrow{MC}+\overrightarrow{MD}=4\overrightarrow{MO}.
			\end{eqnarray*}
			\itemch Ta có $\overrightarrow{AB}\cdot \overrightarrow{AC}=AB\cdot AC\cdot \cos \widehat{BAC}=AB\cdot AC\cdot \dfrac{AB}{AC}=AB^2=16$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[Dự án đề kiểm tra NH24-25 -đợt 7 - Nhật Thiện]%[THPT Thị Xã Quảng Trị  - Quảng Trị]%[0H9H2-5]
	Trong mặt phẳng tọa độ $Oxy$, cho tam giác $ABC$ có $A(-3;0)$, $B(3;0)$ và $C(3;6)$. 
	\choiceTF
	{\True $\overrightarrow{AC}=(6;6)$}
	{$\overrightarrow{AB}\cdot \overrightarrow{BC}=8$}
	{$\overrightarrow{AB}$ vuông góc với trục $Ox$}
	{Điểm $H(1;5)$ là trực tâm của tam giác $ABC$}
	\loigiai{
		\begin{itemchoice}
			\itemch $\overrightarrow{AC}=(3-(-3);6-0)=(6;6)$.
			\itemch Ta có $\overrightarrow{AB}=(6;0)$ và $\overrightarrow{BC}=(0;6)$.\\
			Suy ra $\overrightarrow{AB}\cdot \overrightarrow{BC}=6\cdot 0+0\cdot 6=0$.
			\itemch Xét $\overrightarrow{i}=(1;0)$.\\
			Ta có $\overrightarrow{AB}\cdot \overrightarrow{i}=6\cdot 1+0\cdot 0=6$.\\
			Suy ra $\overrightarrow{AB}$ không vuông góc với $\overrightarrow{i}$.\\
			Vậy $\overrightarrow{AB}$ không vuông góc với trục $Ox$.
			\itemch Gọi $H(x;y)$ là trực tâm của tam giác $ABC$.\\
			Suy ra $\overrightarrow{AH}=(x+3;y)$ và $\overrightarrow{CH}=(x-3;y-6)$.\\
			Ta có 
			\begin{eqnarray*}
				& &\heva{&\overrightarrow{AH}\perp \overrightarrow{BC}\\&\overrightarrow{CH}\perp AB}\\
				&\Leftrightarrow& \heva{&\overrightarrow{AH}\cdot \overrightarrow{BC}=0\\&\overrightarrow{CH}\cdot \overrightarrow{AB}=0}\\
				&\Leftrightarrow& \heva{&(x+3)\cdot 0+y\cdot6=0\\&(x-3)\cdot 6+(y-6)\cdot 0=0}\\
				&\Leftrightarrow& \heva{&y=0\\&x=3.}
			\end{eqnarray*}
			Vậy $H(0;3)$.
		\end{itemchoice}
	}
\end{ex}

\begin{bt}%[Dự án đề kiểm tra NH24-25 -đợt 7 - Nhật Thiện]%[THPT Thị Xã Quảng Trị  - Quảng Trị]%[0H5H3-6]
	Cho tam giác $ABC$ đều có $AB=2a$. Tính $\left|\overrightarrow{AB}+\overrightarrow{AC}\right|$.
	\loigiai{
		Gọi $H$ là trung điểm của $BC$.\\
		Ta có $\overrightarrow{HB}+\overrightarrow{HC}=\overrightarrow{0}\Leftrightarrow \overrightarrow{AB}-\overrightarrow{AH}+\overrightarrow{AC}-\overrightarrow{AH}=\overrightarrow{0}\Leftrightarrow \overrightarrow{AB}+\overrightarrow{AC}=2\overrightarrow{AH}$.\\
		Khi đó $\left|\overrightarrow{AB}+\overrightarrow{AC}\right|=\left|2\overrightarrow{AH}\right|=2AH$.\\
		Do tam giác $ABC$ là tam giác đều nên $AH=\dfrac{AB\sqrt{3}}{2}=a\sqrt{3}$.\\
		Vậy $\left|\overrightarrow{AB}+\overrightarrow{AC}\right|=2a\sqrt{3}$.
	}
\end{bt}

\begin{bt}%[Dự án đề kiểm tra NH24-25 -đợt 7 - Nhật Thiện]%[THPT Thị Xã Quảng Trị  - Quảng Trị]%[0H5H3-9]
	Cho ba lực $\overrightarrow{F}_1=\overrightarrow{MA}$, $\overrightarrow{F}_2=\overrightarrow{MB}$, $\overrightarrow{F}_3=\overrightarrow{MC}$ cùng tác động vào một vật tại điểm $M$ và vật đứng yên. Cho biết cường độ của $\overrightarrow{F}_1$, $\overrightarrow{F}_2$ lần lượt bằng $20$ N, $30$ N và góc $\widehat{AMB}=45^\circ$. Tính cường độ lực của $\overrightarrow{F}_3$.
	\begin{center}
		\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
			\path 
			(0,0) coordinate (M)
			+(20:1) coordinate (A)
			+(-35:2) coordinate (B)
			($(A)+(B)-(M)$) coordinate (D)
			($(D)!2!(M)$) coordinate (C)
			;

			\draw[->] (M)--node[above]{$\overrightarrow{F}_1$}(A);
			\draw[->] (M)--node[below]{$\overrightarrow{F}_2$}(B);
			\draw[->] (M)--node[above]{$\overrightarrow{F}_3$}(C);

			\foreach \p/\r in {A/45,B/-45,C/180,M/-150}
			\draw (\p) node[shift={(\r:3mm)}]{$\p$};
		\end{tikzpicture}
	\end{center}
	\loigiai{
		\begin{center}
			\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
				\path 
				(0,0) coordinate (M)
				+(20:1) coordinate (A)
				+(-35:2) coordinate (B)

				($(A)+(B)-(M)$) coordinate (D)
				($(D)!2!(M)$) coordinate (C)
				;

				\draw[->] (M)--node[above]{$\overrightarrow{F}_1$}(A);
				\draw[->] (M)--node[below]{$\overrightarrow{F}_2$}(B);
				\draw[->] (M)--node[above]{$\overrightarrow{F}_3$}(C);
				\draw (A)--(D)--(B);
				\draw[->] (M)--(D);
				\foreach \p/\r in {A/45,B/-45,C/180,M/-150,D/0}
				\draw (\p) node[shift={(\r:3mm)}]{$\p$};
			\end{tikzpicture}
		\end{center}
		Vật đứng yên nên $\overrightarrow{F}_1+\overrightarrow{F}_2+\overrightarrow{F}_3=\overrightarrow{0}\Rightarrow \overrightarrow{F}_1+\overrightarrow{F}_2=-\overrightarrow{F}_3\Rightarrow \left|\overrightarrow{F}_3\right|=\left|\overrightarrow{F_1}+\overrightarrow{F_2}\right|$.\\
		Xét $\overrightarrow{F}_1+\overrightarrow{F}_2=\overrightarrow{MA}+\overrightarrow{MB}=\overrightarrow{MD}$ với $D$ là đỉnh thứ tư của hình bình hành $AMBD$.\\
		Mặt khác 
		\begin{eqnarray*}
		MD&=&\sqrt{MA^2+MB^2-2MA\cdot MB\cdot \cos \widehat{MAD}}\\
			&=&\sqrt{20^2+30^2-2\cdot 20\cdot 30\cdot \cos 135^\circ}=10\sqrt{13+6\sqrt{2}}.
		\end{eqnarray*}
		Vậy độ lớn của lực $\overrightarrow{F}_3=10\sqrt{13+6\sqrt{2}}$.
	}
\end{bt}

\begin{bt}%[Dự án đề kiểm tra NH24-25 -đợt 7 - Nhật Thiện]%[THPT Thị Xã Quảng Trị  - Quảng Trị]%[0H9H2-7]
