	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			$f(-1) = \dfrac{(-1)^2-(-1)-6}{-1+2}=-4$.
			\itemch \textbf{Sai}.\\
			Ta có $\lim\limits_{x\to (-2)} f(x)=\lim\limits_{x\to (-2)} \dfrac{x^2-x-6}{x+2}=\lim\limits_{x\to (-2)} \dfrac{(x+2)(x-3)}{x+2}=\lim\limits_{x\to (-2)} (x-3)=-5$.\\
			$f(-2)=2a+1$.\\
			Hàm số liên tục tại điểm $x=-2$ khi và chỉ khi \[\lim\limits_{x\to (-2)} f(x)=f(-2)\Leftrightarrow 2a+1=-5\Leftrightarrow a=-3.\]
			\itemch \textbf{Đúng}.\\
			Ta có $\lim\limits_{x\to (-2)} f(x)=\lim\limits_{x\to (-2)} \dfrac{x^2-x-6}{x+2}=\lim\limits_{x\to (-2)} \dfrac{(x+2)(x-3)}{x+2}=\lim\limits_{x\to (-2)} (x-3)=-5$.
			\itemch \textbf{Đúng}.\\
			Tập xác định $\mathscr{D} = \mathbb{R}$.
		\end{itemchoice}
	}
\end{ex}

