\begin{ex}%[1D1H4-6]%[Dự án đề kiểm tra Toán 11 GHKI NH24-25- Nguyễn Hữu Duy]%[THPT Ngô Gia Tự - Đắk Lắk]
Cho hàm số $f(x)=3\sin x+2$.
\choiceTF
{\True Giá trị của hàm số khi $x=\pi$ bằng $2$}
{\True Hàm số $f(x)$ tuần hoàn với chu kì $2\pi$}
{\True Phương trình $f(x)=0$ có tất cả $8$ nghiệm thuộc nửa khoảng $\left[-\dfrac{7\pi}{2};4\pi\right)$}
{Hàm số $f(x)$ có giá trị nhỏ nhất bằng $2$}
\loigiai{
\begin{itemchoice}
	\itemch \textbf{Đúng}.\\
	Ta có $f(\pi)=3\sin \pi +2=2$.
	\itemch	\textbf{Đúng}.\\
	Hàm $y=\sin x$ tuần hoàn với chu kì $2\pi$ nên hàm số $f(x)=3\sin x+2$ tuần hoàn với chu kì $2\pi$.
	\itemch	\textbf{Đúng}.\\
	Ta có $f(x)=0\Leftrightarrow \sin x=-\dfrac{2}{3}$.\\
	Gọi $\alpha\in \left[-\dfrac{\pi}{2};\dfrac{\pi}{2}\right]$ thỏa mãn $\sin \alpha=-\dfrac{2}{3}$. Khi đó ta có 
	\begin{align*}
		\sin x=-\dfrac{2}{3}\Leftrightarrow \sin x=\sin\alpha\Leftrightarrow \hoac{&x=\alpha+k2\pi \\&x=\pi-\alpha+k2\pi} (k\in \mathbb{Z}). 
	\end{align*}
	Tiếp theo ta sẽ đếm số nghiệm thuộc nửa khoảng $\left[-\dfrac{7\pi}{2};4\pi\right)$.
	\begin{itemize}
		\item Với $x=\alpha+k2\pi$, ta có $-\dfrac{7\pi}{2}\leq \alpha+k2\pi <4\pi\Leftrightarrow -1{,}63\leq k< 2{,}11$.\\
		Mà $k\in \mathbb{Z}$ nên $k\in \{-1;0;1;2\}$.
		\item Với $x=\pi-\alpha+k2\pi$, ta có $-\dfrac{7\pi}{2}\leq \pi-\alpha+k2\pi <4\pi\Leftrightarrow -2{,}36\leq k< 1{,}38$.\\
		Mà $k\in \mathbb{Z}$ nên $k\in \{-2;-1;0;1\}$.
	\end{itemize}
	Vậy có tất cả $8$ nghiệm thỏa mãn yêu cầu. 	
	\itemch	\textbf{Sai}.\\
	Ta có $-1\leq \sin x\leq 1$ nên $-3\leq 3\sin x\leq 3$.\\
	Suy ra $-1\leq f(x)\leq 5$.\\
	Vậy hàm số $f(x)$ có giá trị nhỏ nhất bằng $-1$.
\end{itemchoice}	
}
\end{ex}

\begin{ex}%[1D3H3-3]%[Dự án đề kiểm tra Toán 11 GHKI NH24-25- Nguyễn Hữu Duy]%[THPT Ngô Gia Tự - Đắk Lắk]
Cho hàm số $f(x)=\heva{
	&\dfrac{\sqrt{x^2-4x+4}}{x-2} &\text{ khi } x<2\\ 
	&mx^2-3 &\text{ khi } x\geq 2}$ ($m$ là tham số).
\choiceTF
{$\lim\limits_{x\to 2^+}f(x)=1$}
{\True Khi $m=1$ thì $\lim\limits_{x\to 2^+}f(x)=1$}
{\True $f(2)=4m-3$}
{Hàm số $f(x)$ liên tục tại $x=2$ khi $m=1$}
\loigiai{
	\begin{itemchoice}
		\itemch \textbf{Sai}.\\
		Ta có $\lim\limits_{x\to 2^+}f(x)=\lim\limits_{x\to 2^+}\left(mx^2-3\right)=m\cdot 2^2-3=4m-3$.
		\itemch \textbf{Đúng}.\\
		Khi $m=1$ thì $\lim\limits_{x\to 2^+}f(x)=\lim\limits_{x\to 2^+}\left(x^2-3\right)=2^2-3=1$.
		\itemch \textbf{Đúng}.\\
		Dễ thấy $f(2)=4m-3$.
		\itemch \textbf{Sai}.\\
		Ta có $\lim\limits_{x\to 2^-}f(x)=\lim\limits_{x\to 2^-} \dfrac{\sqrt{x^2-4x+4}}{x-2}=\lim\limits_{x\to 2^-} \dfrac{|x-2|}{x-2}=\lim\limits_{x\to 2^-} \dfrac{2-x}{x-2}=-1$.\\
		Để hàm số $f(x)$ liên tục tại $x=2$ thì \begin{eqnarray*}
			&&\lim\limits_{x\to 2^-}f(x)=\lim\limits_{x\to 2^+}f(x)=f(2)\\
			&\Leftrightarrow& 4m-3=-1\\
			&\Leftrightarrow& m=\dfrac{1}{2}.
		\end{eqnarray*}
	\end{itemchoice}
}
\end{ex}

\begin{ex}%[1D3V3-4]%[Dự án đề kiểm tra Toán 11 GHKI NH24-25- Lê Minh Thiện Anh]%[THPT Ngô Gia Tự - Đắk Lắk]
	Cho hàm số $f(x) = \heva{& \dfrac{x^4 + 2x^2 + ax + b}{x-1} & \text{khi } x \neq 1 \\& 3 & \text{khi } x = 1 }$ liên tục trên $\mathbb{R}$. Khi đó giá trị $a-2b$ bằng bao nhiêu?

	\shortans[oly]{-9}
	\loigiai{
		Tập xác định $\mathscr{D}=\mathbb{R}$.\\
		Hàm số $f(x)$ liên tục trên các khoảng $(-\infty;1)$, $(1;+\infty)$.\\
		Ta có $\heva{&f(1)=3\\&\lim\limits_{x\to 1}f(x)=\lim\limits_{x\to 1}\dfrac{x^4+2x^2+ax+b}{x-1}.}$\\
		Hàm số $f(x)$ liên tục trên $\mathbb{R}$ khi và chỉ khi $\lim\limits_{x\to 1}\dfrac{x^4+2x^2+ax+b}{x-1}=3$.\\
		Khi đó 
		\begin{eqnarray*}
		& & \heva{&1^4+2\cdot 1^2+a+b=0\\& \lim\limits_{x\to 1}\dfrac{x^4+2x^2+ax+b}{x-1}=3}\\
		&\Leftrightarrow & \heva{& b=-3-a\\& \lim\limits_{x\to 1}\dfrac{x^4+2x^2+ax-3-a}{x-1}=3}\\
		&\Leftrightarrow & \heva{& b=-3-a\\& \lim\limits_{x\to 1}\dfrac{(x-1)(x^3+x^2+3x+3+a)}{x-1}=3}\\
		&\Leftrightarrow & \heva{& b=-3-a\\& \lim\limits_{x\to 1}(x^3+x^2+3x+3+a)=3}\\
		&\Leftrightarrow & \heva{& b=2\\& a=-5.}
		\end{eqnarray*}
		Vậy $a-2b=-9$.
	}
\end{ex}

\begin{ex}%[1D5H2-3]%[Dự án đề kiểm tra Toán 11 GHKI NH24-25- Lê Minh Thiện Anh]%[THPT Ngô Gia Tự - Đắk Lắk]
