	Cho các hàm số $f(x) = \begin{cases}
		\dfrac{x^2 - 9}{x - 3} & \text{khi } x \neq 3 \\
		7 & \text{khi } x = 3
	\end{cases}$ và $g(x) = \dfrac{2}{x + 1}$.
	\choiceTF[1]
	{\True Hàm số $g(x)$ liên tục tại điểm $x_0 = 3$}
	{$\lim\limits_{x \to 3} f(x) = 7$}
	{Hàm số $f(x)$ liên tục tại điểm $x_0 = 3$}
	{Hàm số $y = f(x) + g(x)$ liên tục tại điểm $x_0 = 3$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Ta có $ \lim\limits_{x \to 3} g(x) = \dfrac{2}{4}=\dfrac{1}{2} \enspace \text{và} \enspace g(3) = 	\dfrac{1}{2}$. \\
			Vì $\lim\limits_{x \to 3} g(x) = g(3)$ nên $g(x)$ liên tục tại $x_0=3$.
			\itemch \textbf{Sai}.\\
			Ta có $\lim\limits_{x \to 3} f(x) = \lim\limits_{x \to 3} (x+3)=3+3=6$.
			\itemch \textbf{Sai}.\\
			$\lim\limits_{x \to 3} f(x) =6 \enspace \text{và} \enspace f(3)=7$. \\
			Vì $\lim\limits_{x \to 3} f(x) \neq f(3)$ nên $f(x)$ không liên tục tại $x_0=3$.
			\itemch \textbf{Sai}.\\
			Do $f(x)$ không liên tục tại điểm $x_0 = 3$ và  $g(x)$ liên tục tại điểm $x_0 = 3$ nên hàm số $y = f(x) + g(x)$ không liên tục tại điểm $x_0 = 3$.
		\end{itemchoice}
	}
\end{ex}

