\begin{ex}%[1D3V3-3]%[Dự án đề kiểm tra Toán 11 HKI Đợt 07 NH24-25 - Quang Vinh NT]%[THPT Số 1 Văn Bàn - Lào Cai] 
	\begin{enumerate}
		\item Tìm giới hạn $\lim\limits_{x\to 2} \dfrac{x^2 - 3x + 2}{x - 2}$.
		\item Cho hàm số $f(x) = \heva{x^2 + 3x - 1 \quad \text{khi} \quad x > 2 \\ mx - 1 \quad \text{khi} \quad x \le 2}$. Tìm giá trị của $m$ để hàm số liên tục tại điểm $x = 2$.
	\end{enumerate}
	\loigiai{
		\begin{enumerate}
			\item Ta có $\lim\limits_{x\to 2} \dfrac{x^2 - 3x + 2}{x - 2} = \lim\limits_{x\to 2} \dfrac{(x-1)(x-2)}{x - 2}= \lim\limits_{x\to 2} (x-1)= 1$.

			\item Ta có 
			\begin{itemize}
				\item $f(2) = 2m - 1$. 
				\item $\lim\limits_{x\to 2^-} f(x) = \lim\limits_{x\to 2^-} (mx - 1) = 2m - 1$.
				\item $\lim\limits_{x\to 2^+} f(x) = \lim\limits_{x\to 2^+} (x^2 + 3x - 1) = 9$.\\
				Hàm số liên tục tại $x = 2$ khi và chỉ khi 
				\begin{eqnarray*}
					&&\lim\limits_{x\to 2^-} f(x) = \lim\limits_{x\to 2^+} f(x) = f(2)\\
					&\Leftrightarrow&  2m - 1 = 9 \Leftrightarrow m = 5.
				\end{eqnarray*}
				Vậy $m = 5$ là giá trị cần tìm.
			\end{itemize}

		\end{enumerate}
	}
\end{ex}

\begin{ex}%[1H4H3-3]%[Dự án đề kiểm tra Toán 11 HKI Đợt 07 NH24-25 - Quang Vinh NT]%[THPT Số 1 Văn Bàn - Lào Cai] 
