\begin{ex}%[1D3H2-3] %[1D3H2-7] %[1D3H3-3] %[Lớp 11 - Ôn tập cuối kì 1]%[Thanh Phát]
	Các mệnh đề sau đúng hay sai?
	\choiceTF
	{\True Hàm số $f(x) = \heva{
			&\dfrac{1 - \sqrt{2x - 3}}{2 - x} & \text{khi } x \ne 2 \\
			&1 & \text{khi } x = 2
		}$ liên tục tại điểm $x_0 = 2$}
	{\True $\lim\limits_{x \to -2} \left(x^2 - x + 3\right) = 9$}
	{\True $\lim\limits_{x \to 1^+} \dfrac{4x - 3}{x - 1} = +\infty$}
	{$\lim\limits_{x \to 2} \dfrac{x^2 - 3x + 2}{x - 2} = 2$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}. Ta có $f(2)=1$. Ta lại có
			\begin{eqnarray*}
				\lim\limits_{x\to 2}f(x)=\lim\limits_{x\to 2} \dfrac{1-\sqrt{2x-3}}{2-x}=\lim\limits_{x\to 2}\dfrac{-2x+4}{(2-x)\left(1+\sqrt{2x-3} \right)}=\lim\limits_{x\to 2}\dfrac{2}{\left(1+\sqrt{2x-3}\right)}=1.
			\end{eqnarray*}
			Suy ra $\lim\limits_{x\to 2}f(x)=f(2)=1$ nên $f(x)$ liên tục tại $x_0=2$.
			\itemch \textbf{Đúng}. Ta có $\lim\limits_{x \to -2} \left(x^2 - x + 3\right) = 9$.
			\itemch \textbf{Đúng}. Vì $\heva{ &\lim\limits_{x \to 1^+} (4x-3)=1>0\\
				&\lim\limits_{x \to 1^+} (x-1)=0\\ &x\to 1^+ \Rightarrow x-1>0}$ nên $\lim\limits_{x \to 1^+} \dfrac{4x - 3}{x - 1} = +\infty$.
			\itemch \textbf{Sai}. Ta có $\lim\limits_{x \to 2} \dfrac{x^2 - 3x + 2}{x - 2} = \lim\limits_{x \to 2} \dfrac{(x-1)(x-2)}{x-2}=\lim\limits_{x \to 2} (x-1)=1$.
		\end{itemchoice}
	}
\end{ex}

