\begin{ex}%[1D3V2-5]%[Lớp 11 - Ôn tập cuối kì 1]%[Congtaoduong]
	Tính giới hạn $\lim\limits_{x \to 0} \dfrac{4x}{3-\sqrt{9+x}}$.
	\shortans[oly]{$-24$}
	\loigiai{
		\begin{eqnarray*}
			\lim\limits_{x \to 0} \dfrac{4x}{3-\sqrt{9+x}}&=&\lim\limits_{x \to 0} \dfrac{4x\left(3+\sqrt{9+x}\right)}{\left(3-\sqrt{9+x}\right)\cdot\left(3+\sqrt{9+x}\right)}\\
			&=&\lim\limits_{x \to 0} \dfrac{4x\left(3+\sqrt{9+x}\right)}{9-9-x}\\
			&=&-\lim\limits_{x \to 0} \dfrac{4x\left(3+\sqrt{9+x}\right)}{x}\\
			&=&-\lim\limits_{x \to 0} \left[4\left(3+\sqrt{9+x}\right)\right]\\
			&=&4\cdot \left(3+\sqrt{9+0}\right)\\
			&=&24.
		\end{eqnarray*}
	}
\end{ex}

\begin{ex}%[1D3V1-2]%[Lớp 11 - Ôn tập cuối kì 1]%[Congtaoduong]
	Tính giới hạn $\lim\limits_{n \to+\infty} \dfrac{5\cdot 2^{n+2}-2\cdot 3^{n+2}}{7+3^{n+1}}$.
	\shortans[oly]{$-6$}
	\loigiai{
		\begin{eqnarray*}
			\lim\limits_{n \to+\infty} \dfrac{5\cdot 2^{n+2}-2\cdot 3^{n+2}}{7+3^{n+1}}&=&\lim\limits_{n \to+\infty} \dfrac{5\cdot 2^2\cdot 2^{n}-2\cdot 3^2\cdot  3^{n}}{7+3\cdot 3^{n}}\\
			&=&\lim\limits_{n \to+\infty} \dfrac{20\cdot 2^{n}-18\cdot   3^{n}}{7+3\cdot 3^{n}}\\
			&=&\lim\limits_{n \to+\infty} \dfrac{3^{n}\left[20\cdot \left(\dfrac{2}{3}\right)^{n}-18\right]}{3^{n}\left[7\cdot\left(\dfrac{1}{3}\right)^n+3\right]}\\
			&=&\lim\limits_{n \to+\infty} \dfrac{20\cdot \left(\dfrac{2}{3}\right)^{n}-18}{7\cdot\left(\dfrac{1}{3}\right)^n+3}\\
			&=&\dfrac{20\cdot 0-18}{7\cdot 0+3}\\
			&=&-6.
		\end{eqnarray*}
	}
\end{ex}

\begin{ex}%[1D3H3-3]%[Lớp 11 - Ôn tập cuối kì 1]%[Congtaoduong]
	Cho hàm số $f(x)=\heva{&\dfrac{\sqrt{x+3}-2}{x-1},\text { khi } x > 1\\
		&-x^2+m,\text { khi } x \leq 1&}$.Với giá trị nào của $m$ thì hàm số liên tục tại $x=1$?
	\shortans[oly]{1,25}
	\loigiai{
		Ta có
		\begin{itemize}
			\item $f(1)=-1^2+m=m-1$.
			\item $\lim\limits_{x \to1^-}f(x)=\lim\limits_{x \to1^-}\left(-x^2+m\right)=m-1$.
			\item $\lim\limits_{x \to1^+}f(x)=\lim\limits_{x \to1^+}\dfrac{\sqrt{x+3}-2}{x-1}=\lim\limits_{x \to1^+}\dfrac{x+3-4}{(x-1)\left(\sqrt{x+3}+2\right)}=\lim\limits_{x \to1^+}\dfrac{1}{\sqrt{x+3}+2}=\dfrac{1}{4}$.
		\end{itemize}
	Do đó, để hàm số liên tục tại $x=1$ thì \begin{eqnarray*}
		f(1)=\lim\limits_{x \to1^-}f(x)=\lim\limits_{x \to1^+}f(x)&\Leftrightarrow& m-1=\dfrac{1}{4}\\
		&\Leftrightarrow& m=\dfrac{5}{4}\\
		&\Leftrightarrow& m=1{,}25.
	\end{eqnarray*}
	}
\end{ex}

