\begin{ex}%[1D2H2-6]%[Lớp 11-HK1-THPT Ngô Gia Tự - Phú Yên]%[Lê Hải Phụng]
	Cho cấp số cộng $(u_n)$ có $u_1 = 5$ và $d = -7$. Khi đó, các mệnh đề sau là đúng hay sai?
	\choiceTF
	{\True $u_2 = -2$}
	{$S_2 = u_1 + u_2 - 7$}
	{\True Số $-849$ là số hạng thứ $123$ của cấp số cộng}
	{Số $-114$ là tổng của $7$ số hạng đầu tiên của cấp số cộng}
	\loigiai{
		\begin{itemchoice}
			\itemch $u_2 = u_1 + d = 5 + (-7) = -2$.
			\itemch $S_2 = u_1 + u_2 = 5 + (-2) = 3$. Ta có $u_1 + u_2 - 7 = 3 - 7 = -4$. Vậy $S_2 \neq u_1 + u_2 - 7$.
			\itemch Giả sử $-849$ là số hạng thứ $n$ của cấp số cộng. Khi đó,
			\begin{align*}
				u_n &= -849 \\
				u_1 + (n-1)d &= -849 \\
				5 + (n-1)(-7) &= -849 \\
				5 - 7n + 7 &= -849 \\
				12 - 7n &= -849 \\
				-7n &= -861 \\
				n &= 123
			\end{align*}
			Vậy số $-849$ là số hạng thứ $123$ của cấp số cộng.
			\itemch Tổng của $7$ số hạng đầu tiên của cấp số cộng là:
			\begin{align*}
				S_7 &= 7u_1 + \dfrac{7(7-1)}{2}d \\
				&= 7(5) + \dfrac{7(6)}{2}(-7) \\
				&= 35 + 21(-7) \\
				&= 35 - 147 \\
				&= -112
			\end{align*}
			Vậy số $-114$ không phải là tổng của $7$ số hạng đầu tiên của cấp số cộng.
		\end{itemchoice}	
	}
\end{ex}

\begin{ex}%[1D3H1-2]%[Lớp 11-HK1-THPT Ngô Gia Tự - Phú Yên]%[Lê Hải Phụng]
	Biết giới hạn $\displaystyle\lim_{n \to +\infty} \dfrac{5n + 10^{2025}}{1 - 2n} = a$. Khi đó, các mệnh đề sau là đúng hay sai?
	\choiceTF
	{\True Giá trị $a$ nhỏ hơn $0$}
	{\True $x = a$ là trục đối xứng của parabol $(P): y = x^2 + 5x + 1$}
	{\True Phương trình lượng giác $\sin x = a$ vô nghiệm}
	{Cho cấp số cộng $(u_n)$ với công sai $d = 3$ và $u_1 = a$, thì $u_3 = 6$}
	\loigiai{Ta có:
		$$ a = \displaystyle\lim_{n \to +\infty} \dfrac{5n + 10^{2025}}{1 - 2n} = \lim_{n \to +\infty} \dfrac{n\left( 5 + \dfrac{10^{2025}}{n}\right) }{n(\left( \dfrac{1}{n} - 2\right) } = \dfrac{5}{-2} = -\dfrac{5}{2}.$$
		\begin{itemchoice}
			\itemch Do đó giá trị $a$ nhỏ hơn $0$.
			\itemch Trục đối xứng của parabol $(P)\colon y = x^2 + 5x + 1$ là $x = -\dfrac{b}{2a} = -\dfrac{5}{2}$.\\
			Vậy $x = a$ là trục đối xứng của parabol $(P)$.
			\itemch Vì $-1 \le \sin x \le 1$ và $a = -\dfrac{5}{2} < -1$ nên phương trình $\sin x = a$ vô nghiệm.
			\itemch Ta có $u_3 = u_1 + 2d = a + 2 \cdot 3 = -\dfrac{5}{2} + 6 = \dfrac{7}{2} \neq 6$.
		\end{itemchoice}	
	}
\end{ex}

